INSERT INTO `guanyin` VALUES (1, '第一签', '上签', '锺离成道', '子宫', '开天辟地作良缘　吉日良时万物全　若得此签非小可　人行忠正帝王宣', '此卦盘古初开天地之象　诸事皆吉也', '急速兆速　年未值时　观音降笔　先报君知', '家宅  祈福　自身  秋冬大利　求财  秋冬大利　交易  成　婚姻  成　六甲  生男　行人  至　田蚕  好　六畜  好　寻人  见　公讼  吉　移徙  失物  东北　疾病  设送　山坟  吉', '锺离成道东汉时人　姓锺离　名权字云房　号正阳　陕西西安咸阳县　仕汉将军　每出兵俱为先锋好杀人　至五十岁计杀人三十余万　北征吐番　发兵二万　均嬴弱士卒　失利尽散　独骑逃至山谷　遇胡僧引入山洞(终南山凝阳洞)　悟人生荣辱得失　後辞官修道　得东华帝君授仙法　遂隐晋州羊角山　汉武帝时　多次礼聘不出　朝廷每有大事　常往咨询　时称山中宰相　吕洞宾之师　八仙之首　正阳派之开山祖师　称为正阳帝君', '天开地辟。结良缘是天地开创之象。为大吉大利之象也。日吉时良。万事俱全顺遂之命。能蒙神指示为此签之人。非小可也。劝君必行中正帝王亦宣言曰。如能行之正直。积善。修身时。必能持之恒久。如宋太祖。赵匡胤之黄袍加身。急速非速言来时值观音降事报与君之者。');
INSERT INTO `guanyin` VALUES (2, '第二签', '中下签', '苏秦不第', '子宫', '鲸鱼未变守江河　不可升腾更望高　异日峥嵘身变化　许君一跃跳龙门', '此卦鲸鱼未变之象　凡事忍耐待时也', '得忍且忍　得耐且耐　须待时至　功名还在', '家宅  不安　自身  还愿　求财  欠利　交易  婚姻  难　六甲  作福　行人  平安　田蚕  吉　六畜  不利　寻人  见　公讼  和　移徙  去　失物  东南　疾病  祈保　山坟  吉', '苏秦不第战国　苏秦　洛阳人　师鬼谷子　游说秦王　书十二上其说　不行　裘敝金尽　憔悴而归　至家妻不下(织)机　(不理睬其夫)嫂不为炊　(不煮饭给其叔)秦惭怒　得太公阴符　发愤苦读　困怠时用锥刺股　痛而再读　後以合从(合纵)之说　联六国抗秦　说赵竟佩六国相印', '鲸鱼未化守江湖。姜太公之受难时也。为大鲸未变之象。凡事进退待时。不可轻举妄动。动则凶。静则吉。君尔也。得忍且忍。得耐且耐身不而托。功名自在者。来日峥嵘蜕身时。一跃可过禹门者');
INSERT INTO `guanyin` VALUES (3, '第三签', '下签', '董永遇仙', '子宫', '临风冒雨去还乡　正是其身似燕儿　衔得坭来欲作垒　到头垒坏复须坭', '此卦燕子衔坭之象　凡事劳心费力也', '千般用计　晨昏不停　谁知此事　到底劳心', '家宅  先凶后吉　自身  谨防　求财  有　交易  成　婚姻  不合　六甲  男　行人  吉　田蚕  利　六畜  利　寻人  见　公讼  有理　移徙  安　失物  见　疾病  还愿　山坟  吉', '董永遇仙汉　董永　千乘人　少失母　独养父　流寓孝感　父亡无以葬　乃从人贷钱一万　日後无钱还　当以身作奴　葬毕　道遇一妇人　求为永妻　永与俱诘　钱主令织缣(锦)三百疋以偿　一月而毕　辞永去　乃曰　我(系)天上织女　缘君至孝　玉帝令我助君偿债　言讫　凌空而去', '冲风冒雨去还归。役役劳心似燕儿。君尔之运逢燕子衔泥筑巢之象耶。空心徒劳。无益者。为先难後甘之运。唯应小心。本签之总结也。千方百计。晨昏鹿鹿不停。谁知此事到底无以可成者。修之。悟之。');
INSERT INTO `guanyin` VALUES (4, '第四签', '上签', '玉莲会十朋', '子宫', '千年古镜复重圆　女再求夫男再婚　自此门庭重改换　更添福禄在儿孙', '此卦串镜重圆之象　凡事劳心有贵也', '淘沙成金　骑龙踏虎　虽是劳心　于中有补', '家宅  欠利　自身  秋冬旺　求财  秋冬旺　交易  允　婚姻  成　六甲  祈保　行人  至　田蚕  可养　六畜  可养　寻人  见　公讼  不利　移徙  守旧　失物  迟见　疾病  祈福　山坟  吉', '玉莲会十朋荆录记　宋朝　钱玉莲与王十朋结婚　十朋上京赴考　孙汝权逼玉莲相嫁　玉莲投河自杀获救　十朋中状元　拒娶丞相之女为妻　二人几经波折　夫妻始复团圆', '菱花镜破镜重圆。为破镜重圆之象。凡事成就吉者也。不是上上者。为中中之签。是先凶後吉之签。交易持公平。求财亦在秋冬转旺。如逢如此之中中运也。可积极为善。积德。当有转吉之机。');
INSERT INTO `guanyin` VALUES (5, '第五签', '中签', '刘晨遇仙', '丑宫', '一锥草地要求泉　努力求之得最难　无意俄然遇知己　相逢携手上青天', '此卦锥地求泉之象　凡事先难后易也', '欲望心事　西方可求　不如莫动　立地可谋', '家宅  祈福　自身  利　求财  利　交易  合　婚姻  合　六甲  安　行人  迟　田蚕  宜早种　六畜  损　寻人  见　公讼  宜和　移徙  吉　失物  不见　疾病  禳星　山坟  吉', '刘晨遇仙东汉　刘晨和阮肇国二人入山采药　遇二仙女　招至成亲　半年後二人回家　怎知至家已过百年　无人认识二人　二人返山找仙女　终不可得　喻有心花开花不发　无心插柳柳成荫', '一锄掘地妥求泉水。此象则表徵君之运图。是凡事谋略之後。劳心方有成就者也。努力向前必有化凶为吉之时。自励奋斗。偶然遇知己。彼时即是君尔。得贵人之刻。在伊人扶持下。必能上青天矣。');
INSERT INTO `guanyin` VALUES (6, '第六签', '中签', '仁贵遇主', '丑宫', '投身岩下　鸟居　须是还他大丈夫　及早营谋谁可得　通行天地此人无', '此卦投岩铜鸟之象　凡事宜顺吉之兆', '食用无鱼　身清喜贫　待时利乐　权与概人', '家宅  不利　自身  中吉　求财  中吉　交易  难　婚姻  平平　六甲  安　行人  近　田蚕  早种　六畜  损　寻人  见　公讼  有理　移徙  吉　失物  见　疾病  设送　山坟  安', '仁贵遇主唐朝　薛仁贵三次投军　只任伙头　唐太宗(李世民)被盖苏追杀　危急之中　得仁贵救　即被唐太宗封为元帅　带兵东征　累立战功', '於岩下饲於菟者。是凡事宜顺则吉之表徵也。得本签之君。应知。为人宜守旧。不可轻举妄动者也。不可贪求非份之外之财。或舍近图远者。皆避免之。予以君知者。使非常清身。岂贫待他时至祥。');
INSERT INTO `guanyin` VALUES (7, '第七签', '下签', '苏娘走难', '丑宫', '奔波阻隔重重险　带水拖坭去度山　更望他乡求用事　千乡万里未回还', '此卦拖坭带水之象　凡事守旧则吉也', '退身可得　进步为难　只宜守旧　莫望高扳', '家宅  不利　自身  祈福　求财  秋冬吉　交易  平平　婚姻  祈福　六甲  祈福　行人  阻　田蚕  不利　六畜  不利　寻人  难　公讼  亏　移徙  吉　失物  不见　疾病  作福　山坟  不利', '苏娘走难周朝景王时　皇后苏英　善与鹦鹉对话　遭西宫梅玉春陷害　潘葛之妻代后受刑　娘娘逃至白马寺　腹痛产子　喻谁无逆境　随遇而安可也', '奔波役役重重险。带水拖泥又渡山之运途也。凡事必须谨慎。准备酌理。探取根源。明其是非者。审一己之进退。方向者。心存恶者得祸。存直者即可为获福矣。祸福者皆由人作之。退身可得进步难为。只宜守田切莫高扳。');
INSERT INTO `guanyin` VALUES (8, '第八签', '上签', '姚能遇仙', '丑宫', '茂林松柏正兴旺　雨雪风霜总莫为　异日忽然成大用　功名成就栋梁材', '此卦此卦松柏茂林之象　凡事有贵气也', '路上亨通　终身有功　田蚕丰熟　家道兴隆', '家宅  吉　自身  顺利　求财  中平　交易  旺　婚姻  成　六甲  男　行人  有信　田蚕  利　六畜  损　寻人  至　公讼  有理　移徙  吉　失物  见　疾病  安　山坟  吉', '姚能遇仙', '本签为松怕之岁寒者。任何之树木枯之後。仍挺具秀者。各面均呈祥。当异口成大材老焉。是大器晚成型。惟不可因此满足。必须因此更加培植。事事积德。方可有更好之明日。要之。象徵平地风光风缘有定。丰稔。田稻家获余庆。');
INSERT INTO `guanyin` VALUES (9, '第九签', '上签', '孔明点将', '寅宫', '烦君勿作私心事　此意偏宜说问公　一片明心光皎洁　宛如皎月正天中', '此卦皎月当空之象　凡事光明通气也', '心中正直　理顺法宽　圣无私语　终有分明', '家宅  吉　自身  顺　求财  中平　交易  旺　婚姻  成　六甲  男　行人  动　田蚕  利　六畜  损　寻人  至　公讼  有理　移徙  吉　失物  祈福　疾病  安　山坟  吉', '孔明点将即诸葛亮　三国时　刘备三顾草庐　出任军师　曹操大将夏侯　领兵来攻　孔明升帐　调兵点将　于博望坡败曹军', '本签者皎月当空之相也。凡事正直则吉之签。虽是前运不佳。前事去之後。渐见顺利者。君尔幸获此签之示者。平素之存心种德之所由成。後得如此之佳境。亦即是心中正直。理顺法宽天无私意莫空虚。');
INSERT INTO `guanyin` VALUES (10, '第十签', '中签', '庞涓观阵', '寅宫', '石藏无价玉和珍　只管他乡外客寻　宛如持灯更觅火　不如收拾枉劳心', '此卦持灯觅火之象　凡事待时成就也', '姻缘会遇　何事不成　须无限意　眼前是真', '家宅  欠利　自身  安　求财  难　交易  难　婚姻  合　六甲  女　行人  动　田蚕  秋吉　六畜  难养　寻人  见　公讼  亏　移徙  旧安　失物  难见　疾病  设送　山坟  中平', '庞涓观阵战国　孙膑齐人　庞涓魏人　同师事鬼谷子　孙膑之学优於庞涓　涓为魏将　与膑斗智　输而忌之　乃以计刖其足　後孙膑假作颠狂　得脱归　齐威王以(膑)为师　将兵伐魏　庞涓至营地观阵　孙膑用减灶添兵之法　赚庞涓　追至马陵道　伏弩射死', '本签者将灯不见火之相。凡事自就之表徵者。逢此下下之时。宜顶天拜地。始可得无灾。是谋望之不成。修吾身後。积德。布施。後必有回天之机。亦即是机缘若遇何事不成春无限前似真此者也。');
INSERT INTO `guanyin` VALUES (11, '十一签', '上签', '书荐姜维', '寅宫', '欲求胜事可非常　争奈亲姻日暂忙　到头竟必成中箭　贵人指引贵人乡', '此卦因祸得福之象　凡事营谋吉利也', '有意兴变　到底安然　若问用事　只近贵人', '家宅  大吉　自身  顺利　求财  有　交易  成　婚姻  合　六甲  男　行人  未至　田蚕  中平　六畜  中平　寻人  见　公讼  有理　移徙  旺　失物  东方　疾病  禳星　山坟  吉', '书荐姜维　(书献姜维)三国时　孔明带兵六出祁山　病重命危　将兵书二十四篇　传大将军姜维　望其秉承遗志', '是因祸致福之签。凡事则吉者也。日欲求好事喜非常。暂时安当守份之时。虽是不合之时。亦可有成之时。贵人自有贵人引导之者。因此。君尔之前途是极光明者。有事盼望到底如斯若问此事贵人提携耶。');
INSERT INTO `guanyin` VALUES (12, '十二签', '上签', '武吉遇师', '寅宫', '否去泰来咫尺间　暂交君子出于山　若逢虎兔佳音信　立志忙中事即闲', '此卦祸中有福之象　凡事先凶后吉也', '换麻得丝　击人双足　要见分明　因灾得福', '家宅  安　自身  吉　求财  有　交易  宜迟　婚姻  成　六甲  男　行人  未回　田蚕  利　六畜  慎吉　寻人  有信　公讼  吉　移徙  守旧　失物  见　疾病  秋吉　山坟  吉', '武吉遇师武吉本是樵夫　姜子牙钓于　溪　二人相遇　见子牙用直钩钓鱼大笑　子牙看武吉气息断曰　今日入城打死人　武吉怒别　担柴入城　误触王相致死　文王画地为牢　武吉後被暂释　照顾老母至秋後正法　老母令其找姜子牙求救　太公收其为徒　授兵法　又令其挖坑施咒破解先天数　秋後不见武吉伏刑　文王演先天数　以为武吉已畏罪自杀　後文王碰见武吉　武吉引文王见姜太公　受封武德将军　师徒二人为文王效力　(故事见于封神榜)', '时临否极泰当来。多年来之恶运。霉道已去。吉星高照之时。事事顺利。一路顺风。毫无阻碍者。将身上抖之擞之。身上之暗埃尽去。尤在卯寅佳信至时。要之。麻换得丝是笑。虽哭要见分明足见为福。');
INSERT INTO `guanyin` VALUES (13, '十三签', '中签', '罗通拜帅', '寅宫', '自小生在富贵家　眼前万物总奢华　蒙君赐紫金腰带　四海声名定可夸', '此卦龙门得通之象　凡事有变大吉也', '囚人逢赦　病即安然　龙门得遇　名遍名都', '家宅  祈福　自身  祈福　求财  秋冬吉　交易  宜迟　婚姻  合　六甲  女　行人  祈保　田蚕  中平　六畜  中平　寻人  见　公讼  有理　移徙  守旧　失物  东方　疾病  安　山坟  吉', '罗通拜帅唐初　罗通十七岁　于校场比武获帅印　领兵北伐扫敌　後触怒唐太宗被免官　获程咬金求情得宽赦　喻少年得志　有成语曰　罗通扫北', '君尔也。自小生身富贵之家。於是君汝之生活也。总是奢华之世界者。谋望皆成。惟有贵人之扶持者。必能称意。且不劳而成者。易言之。囚徙得赦。病遇华陀。龙门得过。名显皇都之象。凡事变化则吉也。');
INSERT INTO `guanyin` VALUES (14, '十四签', '中签', '子牙弃官', '卯宫', '宛如仙鹤出凡笼　脱得凡笼路路通　南北东西无阻隔　任君直上九霄宫', '此卦仙鹤离笼之象　凡事先凶后吉也', '任意无虞　路有亨通　随心自在　逍遥如人(神)', '家宅  不安　自身  还愿　求财  合　交易  合　婚姻  合　六甲  男　行人  见　田蚕  秋利　六畜  损　寻人  阻　公讼  宜和　移徙  吉　失物  东南　疾病  设送　山坟  吉', '子牙弃官商朝纣王时　姜子牙火烧琵琶精後　获司天监职　官拜下大夫　纣王建鹿台　子牙谏王免劳民伤财　不果　逃回宋异人庄(宋家庄)上　欲与妻马氏离朝歌(首府)往西岐(文王首府)　其妻不肯　子牙写了休书　独自往西岐去　(封神传)', '为白鹤冲天之象也。凡事先忧後吉之大吉签。功名成就。生意鼎盛。婚姻完满。财路亨通之时。宜再多阅圣贤书。修吾身。积吾德。必有喜讯连连之日来也。要之。从心无虑远达亨衢道心自在任意所如者耶。');
INSERT INTO `guanyin` VALUES (15, '十五签', '中签', '苏秦得志', '卯宫', '行人　曰气难吞　忽有灾事勿近前　巢破林鸟无所宿　可寻深处稳安身', '此卦鸟鹊巢(离)林之象　凡事到底应心也', '若人得怨　何时可伸　好言不信　守旧待时', '家宅  先凶后吉　自身  谨防　求财  吉　交易  成　婚姻  阻滞　六甲  男　行人  顺　田蚕  利　六畜  旺　寻人  见　公讼  有理　移徙  远可　失物  难见　疾病  还愿　山坟  吉', '苏秦得志战国　苏秦　洛阳人　师鬼谷子　游说秦王　书十二上其说　不行　裘敝金尽　憔悴而归　至家妻不下(织)机　(不理睬其夫)嫂不为炊　(不煮饭给其叔)秦惭怒　得太公阴符　发愤苦读　困怠时用锥刺股　痛而再读　後以合从(合纵)之说　联六国抗秦　说赵竟佩六国相印', '本签示之於**日。触人口气最难吞。如交友中盛气凌人者。令人难以容忍者。亦即是灾厄临时之兆。不可不知也。得罪於人者。如覆巢之祸。爰之。劝君稳便一语存於心。方能有吉。易言之。若得人怨何事可伸。如言不信到底为劳心。巢空之象。因此。凡事守旧则吉者也。');
INSERT INTO `guanyin` VALUES (16, '十六签', '中签', '叶梦熊朝帝', '卯宫', '愁眉思虑暂时开　启出云霄喜自来　宛如粪土中藏玉　良工荐举出尘埃', '此卦阴阳和合之象　凡所谋皆吉也', '得处无失　损中有益　小人逢凶　君子顺吉', '家宅  不利　渐安自身  秋冬吉　求财  成　交易  成　婚姻  成　六甲  虚惊　有宝　行人  至　田蚕  祈福　六畜  平　寻人  见　公讼  不利　移徙  守旧　失物  阻　疾病  作福　山坟  安', '叶梦熊朝帝明朝　嘉靖年进士　万历年间　叶上朝晋见神宗皇帝　求领兵讨伐边寇', '如玉在污泥中之象。如获良玉－贵人一举。牵成时。出污泥则。可获吉也。亦即是表明君之今也。咫尺－眼前之灵即拨开得见日者。时运将到者。曰：得处无伪损中有益顺逆吉凶君子得吉之签。');
INSERT INTO `guanyin` VALUES (17, '十七签', '中下签', '曹操话梅止渴', '卯宫', '莫听闲言说是非　晨昏只好念阿弥　若将狂话为真实　书饼如何止得饥', '此卦书饼充饥之象　诸事多虚少实也', '心中不定　枉看经文　恰似书饼　食也难吞', '家宅  祈福　自身  顺利　不顺　求财  顺利　甭想　交易  顺利　且慢　婚姻  阻　六甲  安　空孕　行人  迟　田蚕  宜早种　六畜  不利　寻人  见　难　公讼  和　勿兴　移徙  守旧　失物  不见　疾病  还愿　山坟  吉', '曹操话梅止渴三国　曹操领兵　败季缺水　兵卒口渴　曹曰前有一梅林　到那当可吃梅解渴　望梅止渴出此　与画饼充饥同意', '如心中不定则看轻也枉费心神是己。何以为之。又如人之妄语信之为真。岂非如画饼之充饥。焉得饱食。因时运之末到。时有吉。时有凶。劝**宜晨昏多念阿弥陀佛。莫听闲言及是非。方可安行。凡事虚少实也。');
INSERT INTO `guanyin` VALUES (18, '十八签', '上签', '曹国舅为仙', '卯宫', '金乌西坠兔东升　日夜循环至古今　僧道得知无不利　士农工商各从心', '此卦阴阳消长之象　凡事遂意之兆也', '心中无事　所作有功　如弓上箭　一发当空', '家宅  安　自身  吉　求财  顺　交易  成　婚姻  合　六甲  男　行人  至　田蚕  早种　六畜  欠利　寻人  至　公讼  有理　移徙  吉　失物  迟见　疾病  拜送　兔年或属兔凶　山坟  大吉', '曹国舅为仙八仙之一　宋朝曹太后之弟　後散尽家财　周济穷人　入终南山修道　遇锺离权及吕洞宾　引入仙班　手持玉箫为其标志', '本签为皎月当夜空。繇东方上升之象。爰之事事亨泰如意者。心中无事。所作皆奏功。又如上弓之一箭。发之即中也。君之今堪称如日月日夜循环恒古今。僧到得之无不利。因此。工商农士无不一一笑颜逐开者。');
INSERT INTO `guanyin` VALUES (19, '十九签', '中签', '子仪封王', '辰宫', '急水滩头放船归　风波作波欲何为　若要安然求稳静　等待浪静过此危', '此卦船行急滩之象　凡事守旧待时也', '急水下船　险处待时　若问营谋　尽(不)可施为', '家宅  作福　自身  作福　求财  未成　交易  未成　婚姻  不就　六甲  虚惊　行人  至　田蚕  吉　六畜  损　寻人  难　公讼  和　勿兴　移徙  守旧　失物  难寻　疾病  祈福　山坟  吉', '子仪封王唐朝大将军　平安禄山和史思明之乱　历七帝　唐肃宗拜为大元帅　封为汾阳郡王　刚八十岁　知有敌侵犯　郭子仪即披挂上阵　八十五卒', '行舟浪洵之象。亦即是险境者。要之。君尔应知目下宜守旧。愿遵守静则吉。妄为则凶也。杨雄之仕王莽之朝为大夫。何以能胜大任。如在急水滩头放水之险行。待时若问营谋。时至方可为。千万叵轻举妄动。');
INSERT INTO `guanyin` VALUES (20, '二十签', '中签', '姜太公遇文王', '辰宫', '当春久雨喜开晴　玉兔金乌渐渐明　旧事消散新事遂　看看一跳遇龙门', '此卦久雨初明之象　凡事遂意也', '神佛护持　有灾无危　途生平安　到底荣归', '家宅  中平　自身  祈福　求财  未遇　交易  待时　婚姻  随意　六甲  女　行人  阻　田蚕  阻　六畜  阻　寻人  迟　公讼  和　移徙  吉　失物  见　疾病  禳星　山坟  吉', '姜太公遇文王周　姜尚　字子牙　汲人(东海许州人)　道号飞熊　先世封於吕　亦曰吕望　避纣乱居东海之滨　钓於　溪(渭水)　其钩为直　意不在鱼　志在君相　文王闻其贤　聘为师(丞相)　(其时年八十)後周伐纣　灭商兴周　武王称曰尚父　封其子丁公於齐', '本签者也。久雨初晴之象。为万事可成之兆。惟得此吉签。必须早植因缘。之後当有莫大利益。君尔在交友之中。宜留意者。必须选中之一为贵人。彼将成王公封爵之贵。将有大成之日。汝必能攀附上。易言之。佛神护佑百事无忧想平生事到底初胜者。');
INSERT INTO `guanyin` VALUES (21, '二一签', '上签', '李旦龙凤配合', '辰宫', '阴阳道合总由天　女嫁男婚喜偎然　但见龙蛇相会合　熊罴入梦乐团圆', '此卦阴阳道合之象　凡事和大吉也', '谋望从心　婚姻孕男　资财进益　更利田蚕', '家宅  安　自身  利　求财  有　交易  成　婚姻  合　六甲  男　行人  至　田蚕  兴　六畜  兴　寻人  见　公讼  有理　移徙  昌盛　失物  得见　疾病  遇良方　山坟  大吉', '李旦龙凤配合李旦为唐睿宗　武则天登基　皇帝贬作平民　流落乡间　巧与胡凤姣相遇　结为夫妇　龙指睿宗　喻婚姻由天也', '君得之签为阴阳道合之签也。凡事和合而大吉者。君逢此为上吉之运。凡事渐获进步。惟不可欲速成。更不得存有欺人之心。今之形成者。获来不易。佳境之时。宜更修之。省之。积吾善可也。易言之。君之今。营谋皆逐。婚姻孕男。资财积聚更吉田亩之收。');
INSERT INTO `guanyin` VALUES (22, '二二签', '中签', '六郎逢救', '巳宫', '旱时田里皆枯槁　谢天甘雨落淋淋　花果草木皆润泽　始知一雨值千金', '此卦旱逢甘雨之象　凡事难中有救也', '田蚕倍熟　命运渐来　病遇良药　行人不便', '家宅  祈保　自身  安　求财  颇有　交易  迟　婚姻  合　六甲  女　行人  有阻　田蚕  利　六畜  利　寻人  难　公讼  亏　移徙  守旧　失物  西方　北方属水　西方属金　疾病  设送　必愈　山坟  安吉', '六郎逢救杨延昭为杨业之六子　称六郎　杨业统率三军　被番邦围困　杨业头撞李陵碑而亡　杨七郎讨救兵被箭射死　六郎撤退至葫芦谷　得五台山和尚(杨五郎)相救出险　和尚相救　喻及时雨也', '本签告之**曰。久旱而逢雨之象。简易之。遇难中有贵人救之象也。因之。凡事遇难。千万不能馁志。不能心灰意懒。垂头丧气。君汝之命中。凡逢难事。必有贵人来扶之。如行人之有阻。但贵人代之。具有行人来之效。要之。田禾能倍收。谋望相宜。病逢妙药。行人使归之好运者也。');
INSERT INTO `guanyin` VALUES (23, '二三签', '中签', '怀德招亲', '巳宫', '欲扳仙桂入蟾宫　普开天门不任君　忽遇一般音信好　人人皆笑岭顶花', '此卦手扳仙桂之象　凡事必有贵人也', '商贾利益　行戴有(无)危　病安讼遂　尽可施为', '家宅  欠安　自身  作福　求财  遂意　交易  合　婚姻  阻　六甲  作福　行人  有危　田蚕  不利　六畜  不利　寻人  难　公讼  亏　移徙  欠吉　失物  疾病  安　山坟  旧吉', '怀德招亲高怀德乃开国功臣　陈桥兵变　皇袍加身　高怀德拥赵匡胤做皇帝　又娶宋太祖赵匡胤之妹　燕国公主为妻　官封驸马都尉　後领兵与杨家将同征辽国', '本签为望仙扳桂之象。凡事必能遇赀人之象者。虽属上平。惟宜多作福。求神庇佑之。方有化凶趋吉之时。到头。君汝谋望一般喜讯好。高人。贵人也。日将佳音报之而来。要之。商贾者有利。行藏无危。病可安。讼可理。尽可施为。');
INSERT INTO `guanyin` VALUES (24, '二四签', '下签', '殷郊遇师', '巳宫', '不成理论不成家　水性痴人似落花　若问君恩须得力　到头方见事如麻', '此卦痴人道塞之象　凡事守旧待时也', '是非莫说　必须仔细　心正理直　方免灾危', '家宅  不安　自身  险　求财  阻　交易  难　婚姻  待时　六甲  祈福　行人  阻　田蚕  多灾　六畜  多灾　寻人  阻　公讼  亏　移徙  吉　失物  难见　疾病  急求神　山坟  不吉', '殷郊遇师殷郊是商朝纣王之子　拜广成子为师　三年後学得法术　下山时曾向广成子宣誓　不帮纣王干坏事　往投靠文王　半途受人唆使回家协助纣王　师父往劝　师徒大打出手　後姜子牙合燃灯道人　把殷郊夹于两山之间　被广成子铲死　以应其誓　(封神榜)', '本签即是痴人丧德之象。凡事守旧待时。君汝目下即是水泡痴人似落花之命也。事事应顺天理。方平安。眼中无人之时。将何。必有福祸来临。不可不慎。亦即是到头毕竟如麻者。爰之。凡事求神作福。祷告。');
INSERT INTO `guanyin` VALUES (25, '二五签', '中签', '姚能受职', '巳宫', '过了忧危事几重　从今再立永无空　宽心自有宽心计　得遇高人立大功', '此卦古井逢泉之象　凡事贵人成就也', '讼终有理　病得安全　出入求谋　古井逢泉', '家宅  平平　自身  阻滞　求财  阻滞　交易  阻滞　婚姻  和合　六甲  有险　行人  阻隔　田蚕  不利　六畜  不利　寻人  阻隔　公讼  有理　移徙  不合　失物  难寻　疾病  祈福　山坟  不安', '姚能受职', '君之签。是宽心自有宽心计。得遇高人－贵人也。方能护圣功。易言之。凡事遇贵人方能吉也。亦即能过了忧危第几重。从今再历永无凶之境。凡事谋略之时必守之。不宜盲目冲之。三思而後行。方不致吃大亏。是到头来讼中有理。病得安癒。出外求外。枯井逢泉者也。');
INSERT INTO `guanyin` VALUES (26, '二六签', '中下签', '钟馗得道', '巳宫', '上下传来事转虚　天边接得一封书　书中许我功名遂　直到终时亦是虚', '此卦虚名之象　凡事虚多少实宜守旧也', '上下心烦　求闲不闲　欲望功名　守旧身安', '家宅  祈福　自身  安　求财  阻　交易  不成　婚姻  不成　六甲  女　行人  有信　田蚕  半熟　六畜  可养　寻人  西方　公讼  亏　移徙  守旧　失物  难　疾病  祈保　山坟  吉', '锺馗得道姓锺名馗　字正南　中南山人氏　唐时人　上京赴考　得状元　但相丑　豹头环眼　铁面　须　唐德宗见怕　欲取消其名衔　宰相卢杞附和　锺馗打卢杞　德宗命拿钟馗　钟馗夺剑自杀　陆贽进谏　封为驱魔大神　锺馗以为鬼魔必在地府　遂往斩之　阎君曰　大凡人鬼之分只在方寸间　心正之鬼可为神　心歪之人即为鬼　姓锺大悟　阴间鬼魅有十殿阎君经理　遂返阳间斩妖除魔　成了捉鬼大王', '是接目虚望之象。宜守旧待时者。不可听信世人之言。宜自吾有主见。以智止谣。何必听人之者。宜向神求之。神灵加护之时。必有转机。因直待终时亦是无者。因此。守之。规谋之。待来日之计用之。易言之。求闲不闻。目下心烦。欲求名利。徒尔虚投者。');
INSERT INTO `guanyin` VALUES (27, '二七签', '中签', '刘基谏主', '午宫', '一谋一用一番书　虑后思前不敢为　时到贵人相助力　如山墙立可安居', '此卦屋好墙壁之象　凡事稳当无险也', '改旧成新　寒花遇春　从前阻滞　今得称心', '家宅  安　自身  吉　求财  有　交易  难　婚姻  阻　六甲  女　行人  迟　田蚕  熟　六畜  利　寻人  迟　公讼  宜和　移徙  吉　失物  无踪　疾病  延安　山坟  守旧大吉', '刘基谏主明朝　青田人　字伯温　通经史　工诗文　精天文兵法　佐朱元璋统一天下　时朱拣领兵往安丰　解除该城之围困　刘基急来劝阻　判断此去必败　如刘基所料　元璋被困　将军徐达及常遇春相救　才得脱险', '本签者。墙有基址之家。凡事接引贵人乡者。君之今也。一谋一事一番机之时。举棋不定之刻。惟君正逢贵人助也。有银墙铁壁所安靠之人。虽有贵人之签。必须慎交。方有向善之效。否则受伊人愚弄。反得败名。');
INSERT INTO `guanyin` VALUES (28, '二八签', '中签', '包公寻李后', '午宫', '东边月上正蝉娟　顷刻云遮亦暗存　或有圆时还有缺　更言非者亦闲言', '此卦月被云遮之象　凡事昏迷未定也', '浮云遮月　不须疑惑　等待云收　便见明白', '家宅  不安　自身  祈保　求财  阻　交易  吉　婚姻  不合　六甲  有惊　行人  滞　田蚕  不利　六畜  不利　寻人  难　公讼  亏　移徙  守旧　失物  西方　疾病  难痊　山坟  改', '包公寻李后朝野史　宋李后(妃)为奸佞郭槐所害　所怀太子生後被换狸猫　包公夜审郭槐　寻回李后　真相大白　李后乃宋真宗妃　给刘妃陷害　把狸猫剥皮去尾　调换太子　李后被打入天牢十年　哭泣至失明　出狱後乞食　包公审此案　真相大白', '本签为月被云遮之象。亦即是凡事。暂时且守旧。待时老。君汝之早年也。事事不遇之时。唯多年辛勤。终能入佳境。事事如意者。好景已迫眉睫。愿君多行善。亦即是言积德。以迓天庥者。君之目前即是浮云遮月不得疑惑。得待云开自见明月时。');
INSERT INTO `guanyin` VALUES (29, '二九签', '中签', '赵子龙救阿斗', '午宫', '宝剑出匣耀光明　在匣全然不惹尘　今得贵人携出现　有威有势众人钦', '此卦宝剑出匣之象　凡事有威有势也', '宝剑出匣　光辉万里　贵人指引　无不观美', '家宅  祈福　自身  吉　求财  利　交易  成　婚姻  好　六甲  女　行人  迟　田蚕  宜早　六畜  损　寻人  见　公讼  和　移徙  兴　失物  南方　疾病  还旧愿　山坟  吉', '赵子龙救阿斗三国　赵云　字子龙　常山人　初依袁绍　後从刘(备)先主　时曹操欲下江南　先攻先主於莘野　先主走樊城　复败於当阳　家眷失散　云於土墙下遇縻夫人　夫人将子阿斗托云　云将马授夫人　夫人不肯授　投井而死　云哭埋毕　怀抱阿斗　杀出重围　(阿斗乃刘备之子即刘禅)', '本签示之曰。是宝剑出匣之象。凡事有高人指出时。必有成者。尽可防身更无疑者。时运已济。安闲守分。至卯年月。大有发展。锋芒毕露者。士子科第。仕官之人。得擢升。另言之。宝剑出匣光辉万里贵人指出。无不赞美者也。');
INSERT INTO `guanyin` VALUES (30, '三十签', '中签', '棋盘大会', '午宫', '劝君切莫向他求　似鹤飞来暗箭投　若去采薪蛇在草　恐遭毒口也忧愁', '此卦安份守己之象　凡事小心谨防也', '闭口含齿　他事莫理　若有亏心　到头害己', '家宅  安　自身  平平　求财  待时　交易  滞　婚姻  未许　六甲  虚惊　行人  阻隔　田蚕  不利　六畜  不利　寻人  阻隔　公讼  莫兴　移徙  守旧　失物  不见　疾病  难痊　山坟  大吉', '棋盘大会唐太宗时　薛仁贵西征被困　仁贵之子薛丁山与其妹往救　至棋盘山　窦一虎劫粮不果　匪妹窦仙童迎战并擒丁山　程咬金做媒　两对兄妹结为夫妇　同往救薛仁贵　喻冤家变亲家　贼匪从良', '本签示之曰。若去采薪蛇伏草。恐遭毒手也堪虞之命途耶。是安身得己之象。凡事暂守旧待时之者。谋事不相宜。一事无成者。田亩。六畜之不利於时。唯山坎是大吉者。易言之。闭口存舌。闲事莫理不可生心恐伤于己。');
INSERT INTO `guanyin` VALUES (31, '三一签', '中签', '佛印会东坡', '未宫', '清闲无忧静处侩　饱后吃茶时坐卧　汝下身心不用忙　必定不招冤与祸', '此卦守旧安然之象　凡事时待时则吉也', '守旧安然　叩问神仙　直待时来　事亦偶然', '家宅  祈保　自身  安　求财  守待　交易  宜迟　婚姻  合　六甲  女　行人  至　田蚕  秋熟　六畜  稳　寻人  难　公讼  和　移徙  吉　失物  虚　疾病  设送　山坟  吉', '佛印会东坡佛印姓谢名端卿　表字觉老　江西饶州府浮梁县人氏　因应举至京　宋神宗因天旱求雨　往大相国寺临坛　谢端卿欲瞻龙颜　东坡允其充作侍者　神宗见捧茶侍者慌忙　问其出处　端卿巧答　皇帝大悦　赐名了元　号佛印　即时披剃为僧　端卿无奈成为和尚　喻人莫无事生事　做事勿慌忙　否则生祸', '本签曰。清闲无事静处坐。饥时吃饭困时卧。可知君尔者当守己安静之时。凡事宜守旧。不宜在此时轻举妄动。动则凶。静则吉之刻。放下身心不用忙。唯有如此。方可除去祸与殃。易言之。守己安静即是待他时之至。必定周全。');
INSERT INTO `guanyin` VALUES (32, '三二签', '中签', '刘备求贤', '未宫', '归程杳杳定无疑　石中藏玉有谁知　一朝良匠分明剖　始觉安然碧玉期', '此卦剖石见玉之象　凡事著力成功也', '宝在石中　异人少知　直待分明　尽可施为', '家宅  平平　自身  安　求财  劳力　交易  阻　婚姻  成　六甲  虚　行人  杳　田蚕  晚成　六畜  旺　寻人  见　公讼  惊　移徙  改　失物  西方　疾病  欠安　山坟  改向吉', '刘备求贤三国时　诸葛亮隐居于隆中之茅屋　刘备求贤　往访三次才得见孔明　谕有宝心安　得意迟早　静待时机　不请自来', '本签示於**曰。君今之命运也。如石中剖玉之象。八百事啻要费力即可成者。易言之。汝是石中藏玉虽知。孜孜不倦中取玉之勤劳人。人生旅途中。常有惊险之象出现。易言之。玉在石中。匠手可知。直待剖出真可施。');
INSERT INTO `guanyin` VALUES (33, '三三签', '中签', '咬金聘仁贵', '未宫', '内藏无价宝和珍　得玉何须外界寻　不如等待高人识　宽心犹且更宽心', '此卦藏玉外寻之象　凡事待时可也', '内藏金玉　不识外寻　遇贵指引　不须劳心', '家宅  祈保　自身  安　求财  守待　交易  迟　婚姻  就　六甲  男　行人  阻　田蚕  半熟　六畜  旺　寻人  劳　公讼  搁　移徙  吉　失物  北方　疾病  遇良医　山坟  宜改', '咬金聘仁贵薛仁贵于山上午睡　突见程咬金路过被虎追　仁贵打虎　救了咬金　程咬金给令箭一枝　著仁贵投军　薛仁贵後成唐朝名将　程咬金为唐之开胝功臣　喻有宝之人　终有出头之日', '又是珍玉内藏之象。是凡事忍耐得吉者也。君汝之今也。谋略不宜之时。乍看似机缘。行之反覆为之。无一事可成。石中既有玉藏。何必向外去寻。亦即是君守於今。自有展谋之时。不宜动。动不如一静。易言之。珍玉内藏不时求。不见待若遇贵人。事则得吉。');
INSERT INTO `guanyin` VALUES (34, '三四签', '中上签', '桃园结义', '未宫', '行藏出入礼义恭　言必忠良信必从　心不了然且静澈　光明红日正当中', '此卦红日当空正照之象　凡事遂意也', '心中无事　秋水澄清　不须疑惑　事自然成', '家宅  清吉　自身  安　求财  虚　交易  难　婚姻  合　六甲  有险　行人  阻　田蚕  不利　六畜  衰　寻人  滞　公讼  亏　移徙  守旧　失物  虚　疾病  设送　山坟  大吉', '桃园结义大丈夫(关公)以四海为家　何患乎吾无兄弟　入桃园　睹两人奇异　请问英雄何处　雄　　　朗曰张飞　貌堂堂　温言刘备　出生投地今逢主　须待挽天河水来荡涤　诚哉龙虎风云会　宰牛马昭告天地　结义匡扶汉室', '本签也。红日当空之象。凡事自来有成。君之今也。出入行藏礼义恭。言必忠良听必聪。与友朋。长辈之交。必须礼恭。天下之事能透彻。君之运也。光明则如日正当空时。易言之。空中无事秋水澄清别无阻碍。自然有成者。');
INSERT INTO `guanyin` VALUES (35, '三五签', '中签', '唐僧取经', '申宫', '衣冠重整旧家风　道是无穹却有功　扫却当途荆棘刺　三人约议再和同', '此卦衣冠重整之象　凡事先难后易也', '不用忧疑　自有佳期　若问前程　前路可宜', '家宅  乱　自身  吉　求财  好　交易  利　婚姻  阻　六甲  惊　行人  至　田蚕  不利　六畜  兴　寻人  至　公讼  有理　移徙  安　失物  迟见　疾病  祈福　山坟  宜旧', '唐僧取经唐三藏信姓陈名　　十三岁出家　法号玄奘　往天竺(印度)取经　历尽艰辛　四年始达　学习十五年後返国　携回大量佛经　并译成中文　西游记内述其故事', '此签是扫却当途荆棘碍。三人共议事相同者。可知重整之象。凡事改变即大吉。功名。交易均可得利。求财。时运。六畜亦均有利可图。是谋略成功。年运亨通之时。一旦称心。万事通之刻。君汝之今为不须忧虑。自有期间。前程事更换可宜。');
INSERT INTO `guanyin` VALUES (36, '三六签', '中签', '湘子遇宝', '申宫', '眼前病讼不须忧　宝地资财尽可求　恰似猿猴金锁脱　自归山洞去来悠', '此卦猿猴脱锁之象　凡事先难后易也', '脱除了事　且自宽心　待得时来　堪寻正路', '家宅  不安　自身  安　求财  大利　交易  待时　婚姻  未成　六甲  祈保　行人  至　田蚕  十足　六畜  平　寻人  至　公讼  有理　移徙  吉　失物  远　疾病  欠安　山坟  吉', '湘子遇宝(宾)韩湘子乃八仙之一　其标志为手持花篮　唐朝诗人　姓韩名湘字若云　湘祖名仲卿　父名会　为韩愈之侄　自少学道　吕洞宾度之登仙　号瑶华帝君', '本签曰。目前病讼不须忧。实地资财尽可求。是上上之签。惟有积德。布施之举。必可更进展之机。如忽尔遇不如意之时。因君汝目下是系猿脱锁之象。凡是通泰。因此。再加修心向善。必有神佑。易言之。且自宽舒待他时至路达通衢也。');
INSERT INTO `guanyin` VALUES (37, '三七签', '中签', '李靖归山', '申宫', '欲待身安动泰时　风中灯烛不相宜　不如收拾深堂坐　庶免光摇静处明', '此卦风摇灯烛之象　凡事守常则吉也', '静处安身　待命守时　支惹非福　守旧相宜', '家宅  守旧　自身  平平　求财  有　交易  难　婚姻  不合　六甲  祈保　行人  有信　田蚕  秋熟　六畜  安　寻人  见　公讼  和　移徙  守旧　失物  疾病  禳星　山坟  勿改', '李靖归山托塔天王李靖　为封神榜人物　塔能镇魔　李靖父子(哪吒)协姜子牙伐纣兴周　周武王立国初论功行赏　李靖父子辞赏归山修道　喻风云(名利)与灯烛(修道)不相合　欲要得道　须弃名利　两者不可兼得', '本签也。风前灯烛之签也。不相宜者。惟凡事小心。谨慎则吉也。君必须收拾深堂坐。亦即是得静为宜吉。如动则凶至。只好暂时忍耐之。时到之时。必有吉祥之象。随君而来。易言之。静处安身命待时泰。动心惹祸。静守救危耶。');
INSERT INTO `guanyin` VALUES (38, '三八签', '下签', '何文秀遇难', '申宫', '月照天宅静处期　忽遭云雾又昏迷　宅有作书宽心祈信云霞散　此时更改好施为', '此卦云雾遮月之象　凡事未遂守旧也', '家道忧凶　人口有灾　祈福保庆　犹恐破财', '家宅  不安　自身  祈保　求财  难　交易  待时　婚姻  难合　六甲  虚惊　行人  阻　田蚕  损　六畜  损　寻人  遇　公讼  和　移徙  安　失物  虚　疾病  延安　山坟  宜修', '何文秀遇难玉钗记　穷书生何文秀琅琅书声　感动了富家女王琼珍　吹箫与之相和　後王女出奔　何文秀被陷入狱　王琼珍剪发改容　历尽艰辛　终成眷属', '本签为浮云遮月之象。目前事事无须如意之时亦即是劝君叵动。俟时运之来至。动之方吉利。唯有汝宽心守待。耐心待至拨云见皎洁月娘时。汝即家居无忧。人口阴灾作福。保佑之。不致有破财之时。守之。不宜不遵行。');
INSERT INTO `guanyin` VALUES (39, '三九签', '下签', '姜女寻夫', '酉宫', '天边消息实难思　切莫多心望强求　若把石头磨作镜　曾知枉费己功夫', '此卦守常安静之象　凡事守己则吉也', '千般用计　枉费功夫　不如守旧　著甚来由', '家宅  安　自身  提防　求财  平平　交易  阻　婚姻  不合　六甲  女　行人  动　田蚕  利　六畜  损　寻人  难　公讼  亏　移徙  平　失物  空　疾病  设送　安　山坟  大吉', '姜女寻夫野史　秦　孟姜女　夫范杞郎(万喜良)　新婚奉命作长城之役　姜女往寻之　及至　知夫已死　欲寻骸骨归葬　神为指处咬指出血　点认夫骨　号恸大哭　长城为之崩　妇人守节　自孟女始', '本签告之於**曰。若把石头磨成镜。精神枉费一时休之安静守旧之象也。一切事宜皆在低潮中。宜小心。小心。如六畜之损。田亩之亏。宜拾旧图新。适合时潮。方能振兴起色。六甲生女亦不必气馁。宜积德。修身为先。必有赖之。易言之。千般用计届时皆空心守旧。不必强求者。');
INSERT INTO `guanyin` VALUES (40, '四十签', '中签', '武则天登位', '酉宫', '红轮西坠兔东升　阴长阳消百事亨　是若女人宜望用　增添财禄福其心', '此卦阴长阳消之象　凡事先难后易也', '阴利女人　不宜男子　若是求谋　先凶后吉', '家宅  不利　自身  祈福　求财  守待　交易  吉　婚姻  不合　六甲  虚  女　行人  阻　田蚕  旺　六畜  旺　寻人  隔　公讼  亏　移徙  大吉　失物  空　疾病  有惊　山坟  大吉', '武则天登位武则天本唐太宗之妃　高宗即位　成为皇后　高宗逝世　上朝摄政　後自立则天皇帝　改国号曰周　执政十五年　颇多建树　但手段卑劣', '本签是阴长阳消之家。凡事先难後吉者。不管是交易。求财。时运。均在开始时。均为不利。但是因一己之奋斗。苦心造诣。终有出头之日。所幸田亩大收。六畜兴旺。皆是祈福所赐。疾病。皆有险。宜小心。易言之。阴利女人。不宜男子。若问求望。无忧从喜。');
INSERT INTO `guanyin` VALUES (41, '四一签', '中签', '董卓收吕布', '酉宫', '无限好言君记取　却为认贼将作子　莫贪眼下有些甜　更虑他年前样看', '此卦认贼作子之象　凡事认真作假', '只这一著　君子莫错　牢记在心　不生寂寞', '家宅  (不)安　自身  平常　求财  守待　交易  难　婚姻  未合　六甲  男　惊　行人  至　田蚕  半熟　六畜  安　寻人  至　公讼  宜和　移徙  祈保　失物  疾病  禳星　山坟  中平', '董卓收吕布三国演义　枭雄董卓　以良马及金钱收卖吕布　又认吕布为义子　後吕布私通董卓爱妾貂蝉　二人交恶　吕布与王允共杀董卓　并劝王允尽诛董卓部属　後吕布败于曹操　被擒杀', '本签示於**曰。认贼为子之象。不宜苟且者。神戒之曰。莫贪眼下有些甜头。趋之。後果堪虞。到头来还是受苦者。功名。交易。尚不理想。求财皆在待时之命运。为认贼作子之时。易言之。只道一著君子莫错宰把身心不致耽搁。');
INSERT INTO `guanyin` VALUES (42, '四二签', '上签', '有莲见母', '酉宫', '君皇圣后终为恩　复待祈禳无损增　一切有情皆受用　人间天上得期亨', '此卦天垂恩泽之象　凡事成就大吉也', '天皇降恩　始终莫忘　晨昏礼念　可宜烧香', '家宅  祈福　自身  冬吉　求财  未有　交易  成　婚姻  中吉　六甲  平安　行人  动　田蚕  中平　六畜  中平　寻人  见　公讼  胜　移徙  守旧　失物  空　疾病  解　山坟  安', '有莲见母', '本签示之曰。天垂甘露之泉。凡事大吉利者。亦即是先难後易者所得。功名成。交易就。婚姻虽平庸。但是难得之缘也。时运至冬季即至。六畜。田亩皆旺之时。宜勤俭。千万不得浪透。陷已於绝境者。易言之。天降甘露。君子莫忘求谋成就。和合吉昌之签也。');
INSERT INTO `guanyin` VALUES (43, '四三签', '上签', '行者得道', '戌宫', '天地变通万物全　自营自养自安然　生罗万象皆精彩　事事如心谢圣贤', '此卦大地交泰之象　凡事大吉无危也', '天生万物　谋望皆通　福德相助　瑞气匆匆', '家宅  吉　自身  安　求财  有　交易  利　婚姻  合　六甲  男　行人  有阻　田蚕  中平　六畜  中平　寻人  见　公讼  胜　移徙  利　失物  东西　疾病  禳星　山坟  东西吉', '行者得道西游记载　行者本为花果山猴王　访求仙法　拜须菩提祖师作师父　祖师赐姓孙　法名悟空　历七年也不知何者为道　祖师打悟空头三下又倒背著手　悟空明白打三下即三更时份　倒背著手指从後门授道　于是祖师授悟空长生之道　又过三年　更修得七十二变　离家二十年後　返回花果山', '本签为天地交泰之象。是事事和谐得称人者。季冬有喜。交春之时。人事遂意者。必是喜事连连之人。万象新之时。自形自色自怡神。事事吉祥之际遇。相机而动者。易言之。本签者天生万物谋望皆新福德积聚瑞气添增。可喜之签也。');
INSERT INTO `guanyin` VALUES (44, '四四签', '中签', '姜维邓艾斗阵', '戌宫', '棋逢敌手著相宜　黑白盘中未决时　皆因一著知胜败　须教自有好推宜', '此卦棋逢敌手之象　凡事用机关则吉也', '欲求心事　恰如围棋　到底胜利　宜用心机', '家宅  大利　自身  平稳　求财  遂　交易  利　婚姻  未合　六甲  女　行人  阻　田蚕  平　六畜  损　寻人  阻　公讼  宜和　移徙  守旧　失物  空　疾病  祈保　山坟  守旧', '姜维邓艾斗阵三国时　姜维(蜀)与邓艾(魏)会师祈山　二人皆摆八卦阵　姜维又摆长蛇阵　邓艾被困　得司马望救出　喻世事如棋　一子莫错', '本签为棋逢敌手之象也。凡事先吉。凡未小心。节俭则有转恶之嫌。宜小心为之。棋逢敌手之时。必藏有机。决定胜负之时莫迟疑。认为可行则毫不犹豫之下即可。尤应和者。善有善报。恶有恶报。示人早知觉悟多作善。方能有善报者。');
INSERT INTO `guanyin` VALUES (45, '四五签', '上签', '仁宗遇仙', '戌宫', '温柔自古胜刚强　积善之门大吉昌　若是有人占此卦　宛如正渴遇瑶浆', '此卦积善温柔之象　凡事贵人和合也', '天地有感　应验非常　佛神护佑　得之莫忘', '家宅  吉　自身  安　求财  遂　交易  利　婚姻  合　六甲  男　行人  阻　田蚕  秋利　六畜  中平　寻人  见　公讼  胜　移徙  利　失物  东方　疾病  祈福　山坟  东西向吉', '仁宗遇仙', '本签为积福门庭之象。凡事谋则吉者也。灵签曰。温柔自是胜强刚。积善于门大吉昌。为使己之事务。求财。时运吉昌者。必须积善。修德。布施。方能亨之。久而弥坚。易言之。天地有感应验非常佛神保持得福寿长之签。');
INSERT INTO `guanyin` VALUES (46, '四六签', '中签', '渭水钓鱼', '戌宫', '勤君耐守旧生涯　把定心肠勿起歹　直待有人轻著力　枯枝老树再生花', '此卦枯木生花之象　凡事自有成就也', '守旧守旧　事自成就　动则多殃　静则无咎', '家宅  安　自身  吉　求财  待时　交易  成　婚姻  就　六甲  生女　行人  阻　田蚕  五分　六畜  难　寻人  杳　公讼  吉　移徙  守旧　失物  空　疾病  求福　山坟  吉', '渭水钓鱼周　姜尚　字子牙　汲人(东海许州人)　道号飞熊　先世封於吕　亦曰吕望　避纣乱居东海之滨　钓於　溪(渭水)　其钩为直　意不在鱼　志在君相　文王闻其贤　聘为师(丞相)　(其时年八十)後周伐纣　灭商兴周　武王称曰尚父　封其子丁公於齐　喻静待时机也', '本签为枯木生花之家。即是万物逢春复苏之象。君尔多年辛劳得报於斯时。惟早植因缘。必有喜事连连。诗日：劝君耐守旧生涯。爰之。君之今宜静。不宜动。动则凶。静则吉。亦方能枯木再开花。向使逢遇贵人。可攀之。伊者後日之诸侯。易言之。决心守旧时至成就行多多逆。安静无忧。');
INSERT INTO `guanyin` VALUES (47, '四七签', '上签', '梁灏登科', '亥宫', '锦上添花色愈鲜　运来禄马喜双全　时人莫恨功名晚　一举登科四海传', '此卦锦上添花之象　凡事大吉大利也', '守旧变新　运至材成　遂平生志　尽皆是命', '家宅  祈保  吉　自身  安　求财  利　交易  阻　婚姻  合　六甲  生男　行人  迟　田蚕  八分　六畜  平　寻人  至　公讼  宜和　移徙  吉　失物  西北　疾病  作福　山坟  利', '梁灏登科宋朝　书生梁灏字太素　山东人氏　雍熙年间　十八岁考得进士　继续投考未遂　至老年(八十)才一举登科　状元及第', '本签者。福禄双全之象者。凡事兴隆大吉之签。曰锦上添花色更鲜。运来之时也喜双全。八十二对大魁。不是晚也。奉献社会人群。积吾德。荫子孙。何乐不为。为天数之所赐。宜再积德。方有同点灯之故。易言之。儿有双亲。守时待庆遂平生志。都来自命。');
INSERT INTO `guanyin` VALUES (48, '四八签', '中签', '韩信挂帅', '亥宫', '昆鸟秋来化作鹏　好游快乐喜飞腾　翱翔万里云霄去　余外诸禽终不能', '此卦昆鹏兴变之象　凡事有变动事', '昆化为鹏　万禽不能　桂香一折　福禄自增', '家宅  利　自身  吉　求财  秋旺　交易  迟　婚姻  不合　六甲  安　行人  阻滞　田蚕  中平　六畜  春利　寻人  见　公讼  胜　移徙  守旧　失物  西　疾病  祈保　山坟  安', '韩信挂帅韩信　淮阴人　时汉王烧绝栈道　部下将士逃亡甚多　其时　信归汉逃去　何知之　连夜追信回　翌日王知　怒曰将士逃亡过半　不闻丞相有所追　独追信　何也　何曰大王如无意东伐　固罢　若与楚争天下　非信莫当其任　乃拜信为大将　佐汉高帝定天下　虏魏　破赵　降燕　下齐　灭楚　建十大功勋　初封楚王', '本签为昆鸟乌化鹏之吉象也。凡事皆能兴隆大吉。功名成之。利得之。婚合之。六畜旺之。六甲全之。官事。等等。无一不顺利也。直冲云霄之外。无可与伦比者。惟逢此佳境。勿忘今之所由成。积德。布施之再行者。可维护君尔之谋望无疑。易言之。今之君也。桂花时节财禄成此。');
INSERT INTO `guanyin` VALUES (49, '四九签', '中签', '王祥求鲤', '亥宫', '天寒地冻水成冰　何须贪吝取功名　只好守己静处坐　待叶兴盛自然明', '此卦水结成冰之象　凡事不用枉求也', '水结成冰　冰消成水　若是营谋　到底如是', '家宅  不安　自身  祈保　求财  难　交易  阻　婚姻  不合　六甲  险　行人  迟　田蚕  亏　六畜  损　寻人  见　公讼  和　移徙  守旧　失物  空　疾病  难痊　山坟  大吉', '王祥求鲤晋　王祥字伏徵　临沂人　性至孝　事继母极恭　谨母欲食生鱼　天寒冰冻　祥赤身卧於冰上　求之　其冰立解　得双鲤　持归　人以为孝感云　後官至太保　进公爵　(廿四孝)', '');
INSERT INTO `guanyin` VALUES (50, '五十签', '中签', '陶朱归五湖', '亥宫', '五湖四海任君行　高挂帆蓬自在撑　若得顺风随即至　满船宝贝喜层层', '此卦顺风撑船之象　凡事皆顺大吉也', '自在休悠　不须著力　财禄重重　无不欣美', '家宅  利　自身  吉  待运至　求财  利西　交易  宜迟　婚姻  不合　六甲  安　行人  阻　田蚕  秋利　六畜  春旺　寻人  至　公讼  亏　移徙  守旧　失物  西方　疾病  解　山坟  南利', '陶朱归五湖　(范蠡归湖)春秋　范蠡　字少伯　吴人　与文种同为我大夫　吴灭越　蠡与种　为越王勾践画策　卑词请成　复求民间美女　得西施进於吴　迨灭吴　功成　蠡知勾践可与同患难　不可共安乐　乃辞官　扁舟泛五湖　止于陶　复以谋生致富　称陶朱公　凡三徙　方成名　後不知所终', '');
INSERT INTO `guanyin` VALUES (51, '五一签', '中上签', '孔明入川', '子宫', '夏日炎天日最长　人人愁热闷非常　天地也解知人意　薰风拂拂自然凉', '此卦人人愁热之象　凡事随心从意也', '进退莫疑　自有佳期　营谋用麽　不须妄为', '家宅  祈福　自身  谨防　凶中有吉　求财  秋吉　交易  平常　婚姻  合　六甲  生男　行人  动　田蚕  损　六畜  平　寻人  见　公讼  吉　移徙  旧　失物  北方　疾病  还愿　山坟  吉', '孔明入川刘备缺地盘　时益州的刘璋　被北方的张鲁攻打　想到刘备与他同姓同宗　就让刘备入益州同抗张鲁　孔明随刘备入益州　详察地势　西川一带　地形险要可攻可守　经孔明多方布置　西川就成了刘备发源地', '本签之诗也。炎天生凉之家。凡事所谋稳当者。虽是中平之签。人生之乐在於此。凡事知足之时即大安。谋事不宜因小嫌弃。改大则败者。一石拾之嫌小。舍之。再拾之。无一而足者。到头无石可捡。易言之。进用莫疑。最佳期日至。凡成谋望。不须忧虑。');
INSERT INTO `guanyin` VALUES (52, '五二签', '中下签', '太白醉捞明月', '子宫', '水中捉月费功夫　费尽功夫却又无　莫说闲言并乱语　枉劳心力强身枯', '此卦贪求费力之象　凡事劳心费力也', '言诺难思　费力劳心　宅中忧虑　等遇贵人', '家宅  不安　自身  欠吉  凶　注意健康　求财  中平  休想交易  难　婚姻  未就　六甲  祈保　空宝　防小产　行人  有灾　田蚕  五分　六畜  衰　寻人  至  凶　公讼  和  即止讼　移徙  平常  甭提　失物  空　疾病  祈保  凶　山坟  吉', '太白醉捞明月唐朝诗人　自曰谪仙　被称诗仙　无酒不欢　醉酒到湖边　见月在水中　欲抱之　溺毙　年六十二岁', '');
INSERT INTO `guanyin` VALUES (53, '五三签', '中签', '刘备招亲', '子宫', '失意番成得意时　龙吟虎啸两相宜　青天自有通霄路　许我功名再有期', '此卦龙吟虎啸之象　凡事顺意有望也', '教去即去　自有亨通　到头必制　立地可谋', '家宅  不安　自身  欠吉　先凶后吉　求财  中平　交易  难　婚姻  成就　六甲  祈保　行人  至　田蚕  五分　六畜  损　寻人  至　公讼  和　移徙  吉　失物  见　疾病  安　山坟  吉', '刘备招亲刘玄德　即西蜀先主也　为荆州牧时　甘夫人(刘备妻)新丧　东吴孙权闻知　诈以妹招赘欲赚(软禁)其人　吴素讨荆州　孔明料知　乃授赵云锦囊计　保先主入吴　(东江招亲)意弄假成真　娶孙夫人以归焉　有曰周郎妙计高天下　赔了夫人又折兵', '本签之诗也。炎天生凉之家。凡事所谋稳当者。虽是中平之签。人生之乐在於此。凡事知足之时即大安。谋事不宜因小嫌弃。改大则败者。一石拾之嫌小。舍之。再拾之。无一而足者。到头无石可捡。易言之。进用莫疑。最佳期日至。凡成谋望。不须忧虑。');
INSERT INTO `guanyin` VALUES (54, '五四签', '下签', '马超追曹', '子宫', '梦中得宝醒来无　自谓南山只是锄　若问婚姻并问病　别寻来路为相扶', '此卦梦中得宝之象　凡事枉费心力也', '物在水中　形影长短　皆有分定　不用商量', '家宅  欠安　自身  谨防　求财  阻　交易  难　婚姻  待时　六甲  祈保　行人  迟　田蚕  滞　六畜  滞　寻人  见　公讼  亏　移徙  吉　失物  空　疾病  求神保佑　山坟  不安', '马超追曹三国时　曹操杀马超之父　马超攻曹操　曹操大败　曹操脱下锦衣　割短了胡须　骑马逃窜　得曹洪相救　曹操得以保命', '本签曰。梦中得宝醒来无。可知君之谋望。均归虚者也。功名。交易。婚姻。求财等。皆守旧方可。目下之所由成者积德。修身之不足所由生。爰之。更在修阴骘方面多下功夫。必有转机之时。前程方可远大。易言之。本签也。戒人去恶从善。趋吉化凶者也。');
INSERT INTO `guanyin` VALUES (55, '五五签', '中上签', '周武王登位', '丑宫', '父贤传子子传孙　衣食丰隆只靠天　堂上椿萱人快乐　饥饭渴饮困时眠', '此卦接竹引泉之象　凡事谋望大吉也', '接竹引泉　流传不绝　君子谋望　无不欣悦', '家宅  欠安　大吉　自身  还愿　大吉　求财  利　交易  成　婚姻  合　遇佳婿　六甲  平安　生肖子　行人  动　田蚕  吉　六畜  损　寻人  难　见　公讼  有贵人　移徙  吉　失物  见　疾病  禳星　得祖荫　山坟  安', '周武王登位周武王伐纣　八百诸侯战商军　朝歌兵卒倒戈相向　纣王于摘星楼上自焚　商朝灭亡　武王受册宝　即天子位　面南垂拱端坐　武王命散鹿台之财　拆毁摘星楼　发钜桥之粟　释箕子之囚　封比干之墓　式商容之闾　放内宫之人　又偃武修文　归马于南山之阳　放牛于桃林之野　一片安乐景象', '本签示於**曰。子孙贤。衣禄丰盈富在天。君尔之命。即是富贵天定者。大吉大利之签者。功名。交易。婚姻。求财皆如意者。而且吃饭困时眠之运。逢此大吉利之时。宜多修德。积善。更可发阳光大者。');
INSERT INTO `guanyin` VALUES (56, '五六签', '中签', '禄山谋反', '丑宫', '滩小石溪流水响　风清明月贵人忙　路须借问何方去　莫取林中花草香', '此卦船行小滩之象　凡事有贵人助也', '营谋用事　劳心费力　守旧待时　凡事大吉', '家宅  安　自身  吉　求财  中平　交易  利　婚姻  遂　慎　六甲  男　行人  至　田蚕  秋利　六畜  损　寻人  至　公讼  有理　移徙  吉　失物  带　疾病  即安　山坟  宜修', '禄山谋反营州柳城　胡人　本姓康　字轧荦山　後冒姓安　更名禄山　唐玄宗时　安禄山为节度使　与杨贵妃友好　与杨国忠有隙　于山西举兵谋反　攻打洛阳　又陷潼关　改国号燕　皇帝西逃至四川(蜀)　安禄山于长安烧杀抢掠　後被自己之子所杀', '本签是涧水行船之象。是凡事先难後吉者。虽是君尔力劳撑驾。恐有损伤。先是劳而无功。路顺指出前江去。自有风静浪平。一路顺风之时。必须更修身。积善。布施。天心感应之时。必有更上一层楼之列。易言之。涧小滩多掌船劳力指出前江风帆顺得。');
INSERT INTO `guanyin` VALUES (57, '五七签', '中签', '董仲寻亲', '丑宫', '说是说非风过耳　好衣好禄自然丰　君莫记取当年事　汝意还如我意同', '此卦孩儿见母之象　诸事贵人大吉也', '所用谋望　皆大欢喜　从公用力　如子逢娘', '家宅  不安　自身  平平　求财  顺　交易  成　婚姻  合　六甲  女　行人  阻　田蚕  就　六畜  损　寻人  难　公讼  宜和　移徙  守旧　失物  空　疾病  祈福　山坟  吉', '董仲欲寻母　至太白山找　终见其母', '本签者好子见娘之象。凡事能遇贵人则化吉也。凡遇事可受贵人扶之。提携之後可成。必为其人所敬仰。一倡百和者。事事可以成功。好衣好禄日当空。易言之。选用谋望皆大吉昌。从心切恳。如子见娘。');
INSERT INTO `guanyin` VALUES (58, '五八签', '中签', '文王问卜', '丑宫', '直言说话君须记　莫在他乡求别艺　切须守己旧生涯　除是其余都不利', '此卦守旧守时之象　凡事守旧则吉也', '得住且住　可取且踞　莫宜别事　只宜守旧', '家宅  平常　自身  不利　求财  吉　交易  成　婚姻  阻　六甲  惊　行人  迟　田蚕  五分　六畜  损　寻人  见　公讼  亏　移徙  守旧　失物  凶　疾病  欠安　山坟  宜改', '文王问卜周文王善卜卦　纣王召见　文王卜之不吉　必有七年之厄　严命其长子依旧法治岐　切莫往外　文王果被纣王软禁于　里　後其子往救其父被杀　皆因忘父命离乡也　文王于　里从旧艺　编修易经　故文王也是易经作者之一也　(另二位作者是同公与孔子)', '本签者。耍君尔必守旧生涯之象。凡事必须三思而後行。不宜轻举妄动。诗曰。忠言善语君须记。千万因不满现况而见异思迁。一股干劲。结果带来不可收抬之残局。是时悟知。不如不作。即时已晚。噬脐不及者。易言之。得往且往可往旧路温故知新。莫求别路。');
INSERT INTO `guanyin` VALUES (59, '五九签', '中下签', '张良隐山', '寅宫', '直上重楼去藏身　四围荆棘绕为林　天高君命长和短　得一番成失二人', '此卦守旧随时之象　凡事待时则吉', '如醉饮酒　只宜守旧　直待时来　无灾无咎', '家宅  祈福　自身  安　运滞　唯仁可解　求财  平常　切忌　交易  成　且慢　婚姻  不合　六甲  作福　行人  至　田蚕  半吉　六畜  大利　失利　寻人  阻　公讼  亏　移徙  吉　不吉　失物  凶　疾病  祈福　山坟  吉', '张良隐山汉　张良　字子房　韩人　其先五世相韩　秦灭韩　良散家财遁迹　欲为韩报仇　乃使力士怀铁锥　击始皇於博浪沙上　误中副车　始皇大索天下　十月不获　後左(佐)汉定天下　功成谢绝人事　托名与赤松子游', 's');
INSERT INTO `guanyin` VALUES (60, '六十签', '下签', '赤壁鏖兵', '寅宫', '抱薪救火大皆燃　烧遍三千亦复然　若问荣华并出入　不如收拾枉劳心', '此卦抱薪救火之象　凡事亦自谨防也', '有千般计　一旦相惹　不如莫动　恐惹祸身', '家宅  凶　自身  祈保　求财  微微　交易  不合　婚姻  不合　六甲  险　行人  滞　田蚕  不利　六畜  损　寻人  杳　公讼  亏　移徙  吉　失物  西方　疾病  有惊　山坟  平平', '赤壁鏖兵三国时　曹操大军驻赤壁岸边　孙权将周瑜　联刘备军抗曹　孔明借东风　黄盖引火烧连环船　二军激战　一片火海　曹军大败　史称赤壁之战　(鏖指烧杀)', '本签示之於**曰。抱薪救火。当然火会增烟。霉头加以霉繇此可知。君尔凡是著手兴事。必须守旧。千万不能逞一时之勇。见人之成。见异思迁。轻举妄动。恐怕有大害。不仅不能成事。反而将事弄砸。得不偿失。易言之。总有千般。一旦皆休。安心守旧维得无忧。');
INSERT INTO `guanyin` VALUES (61, '六一签', '中签', '苏小妹难夫', '寅宫', '日落吟诗月下歌　逢场作戏笑呵呵　相逢会遇难藏避　唱彩齐唱连理罗', '此卦守旧随时之象　凡事时吉利也', '佳人在屋　吟唱诗曲　一片欢心　命中祸福', '家宅  安　自身  吉　求财  顺　交易  利　婚姻  成　六甲  女　行人  至　田蚕  熟　六畜  旺　寻人  至　公讼  有理　移徙  吉　失物  凶　疾病  即痊愈　山坟  大吉', '苏小妹难夫宋朝　苏小妹即苏东坡之妹　博学多才　新婚之夜　苏小妹吟诗又出对联　备三个难题考夫婿　有意刁难新郎秦少游　至第三题苦思著　幸好得苏东坡暗助　才可洞房　有颂曰　文章自古说三苏　小妹聪明胜丈夫　三难新郎真异事　一间秀气世间无　(见于恒世恒言)', '本签之示於**曰。日上吟诗日下歌者。为佳人欢乐之象者。凡是百事兴。大吉昌之徵。因时运知。所谋之事也。百事如意之时。惟将如此康泰上盈之道。何如保之。冤之。保之。方能克终。易言之。君之今也。佳人在屋唱。吟诗曲一片欢嘉取乐且足之佳境。');
INSERT INTO `guanyin` VALUES (62, '六二签', '中签', '唐僧得道', '寅宫', '晨昏传籁佛扶持　须是逢危却不危　若得贵人相引处　那时财帛亦相随', '此卦神佛暗佑之象　凡事忍耐大吉也', '平平稳稳　四方名显　改旧从新　亦无凶险', '家宅  安　自身  吉　求财  西方　交易  遂　婚姻  成　六甲  祈福　行人  阻　田蚕  七分　六畜  利　寻人  西方　公讼  亏　移徙  守旧　失物  见　疾病  祈福　山坟  吉', '唐僧得道一天来到河边　佛祖灵山在望　有一独木桥　唐僧不敢走过　佛祖化身船夫并撑著无底破船　唐僧跌下河中　被船夫救起　唐僧就此得证佛道了', '本签也。守旧平安之象。凡兴事。能遇贵人扶持之时。必能顺利者。晨昏全赖佛祖扶持。因之。必须先由吾身之修德。积善始。神方能有所庇佑。凡事须隐忍。藏器以待用。不宜急。不可躁。易言之。君之命也。诸事平稳四方名显。改换从新。凶存吉现也。');
INSERT INTO `guanyin` VALUES (63, '六三签', '中签', '女娲氏炼石', '卯宫', '昔然行船失了针　今朝依旧海中寻　若然寻得原针在　也费工夫也费心', '此卦海中寻针之象　凡事费心劳力也', '福是缘基　缘是福种　说得分明　切宜守谨', '家宅  欠利　自身  谨防　求财  有　交易  成　婚姻  不合　六甲  阻　行人  滞　田蚕  吉　六畜  莫养　寻人  难　公讼  宜和　移徙  宜迁　失物  凶　疾病  难痊　山坟  暂安', '女娲氏炼石淮南子　览冥篇　往古之时　四极废　九州裂　天不兼覆　地不周载　火　炎而不灭　水浩洋而不息　猛兽食颛民　鸷鸟攫老弱　于是女娲炼五色石以补苍天　断鳌足以立四极　杀黑龙以济冀州　积芦灰以止淫水　苍天补　四极正　淫水涸　冀州平　狡虫死　颛民生　背方州　抱圆天　当此之时　禽兽虫蛇　无不匿其爪牙　藏其蜇毒　无有攫噬之心　考其功烈　上际九天　下契黄垆　名声被後世　光晖熏万物', '本签也。海底捞针之象。凡事费劳空力之徵。成为如此者。时也命也。不得违之。逞一时之强。却不得任何结果。反而受到不利。甚至吃亏。蚀本之情形出现。福是福基福埋福种。说得分明且宜手拱。');
INSERT INTO `guanyin` VALUES (64, '六四签', '下签', '马前覆水', '卯宫', '游鱼却在碧波池　撞遭罗网四边围　思量无计番身出　事到头来惹事非', '此卦鱼遭罗网之象　凡事亦宜提防也', '屋下安身　祸从天降　早觉先防　免遭灾殃', '家宅  凶　自身  险　求财  虚　交易  中平　婚姻  不合　六甲  惊　行人  有阻　田蚕  损　六畜  衰　寻人  杳　公讼  亏　移徙  吉　失物  凶　疾病  有灾险　山坟  宜改', '马前覆水　(覆水难收)汉朝　名臣姓朱　名买臣　表字翁子　会稽郡人氏　家贫未遇　夫妻二口住于陋巷蓬门　砍柴卖钱度日　卖柴凭人估值　不争价钱　其妻出门汲水　见群儿嬉笑买臣　深以为耻　其妻後去　五十岁时　汉武帝求贤　拜为会稽太守　其妻自悔有眼无珠　愿降为婢妾　伏事终身　买臣命取水一桶　泼于阶下曰　若水可收　则可复合　念结发之情　判後园隙地与妻及其夫耕种自食　其妻遂投河而死　故事  买臣五十富贵　汉　朱买臣　会稽人　家贫　卖薪自给　行歌诵书　妻羞之　求去　买臣曰　吾年五十当富贵　即时自可报汝　妻不听　适田夫後　买臣果五十为会稽太守　妻与田夫治道迎官　买臣驻车呼之　以车载其夫妇　舍园中给食一月　妻惭自缢　与夫钱以葬之', '本签者暗遭罗网四边围之命者。凡事必须提防歹人陷害。此运者起跌不常者。且虚。险。惊。改。凶之者多。经多方面涉尘。终能出险。惟此历程艰苦异常。无以数计。思量无计翻身出。易言之。身居尘内祸自天来。早须防备免受其灾。');
INSERT INTO `guanyin` VALUES (65, '六五签', '下签', '孙膑困庞涓', '卯宫', '眼前欢喜未为欢　亦不危时亦不安　割肉补疮为甚事　不如守旧待时光', '此卦割肉成疮之象　凡事只宜守旧待时', '知止则止　知宽自宽　割自身肉　疾痛一般', '家宅  不安　自身  口舌　求财  不利　交易  小人　婚姻  不长　六甲  有惊　行人  阻　田蚕  损　六畜  损　寻人  招非　公讼  亏　移徙  守旧　失物  难寻　疾病  反覆　山坟', '孙膑困庞涓战国　孙膑齐人　庞涓魏人　同师事鬼谷子　孙膑之学优於庞涓　涓为魏将　与膑斗智　输而忌之　乃以计刖其足　後孙膑假作颠狂　得脱归　齐威王以(膑)为师　将兵伐魏　用减灶添兵之法　赚庞涓　追至马陵道　伏弩射死', '本签者肉成疮之象者。凡事必须守旧待事。不宜轻举妄动者也。因时命之注定君尔之命眼欢喜未为欢。危不危兮安不安。日下运多拂逆。毫无利息可言。如百花凋落。无复荣华之望。几希万事皆空之势。易言之。如得止。且止知欢割自己肉痛一般。');
INSERT INTO `guanyin` VALUES (66, '六六签', '下签', '霸王被困', '卯宫', '路险马嬴人行急　失群军卒困相当　滩高风浪船棹破　日暮花残天降霜', '此卦船破下滩之象　凡事险阻提防也', '守旧守旧　安静无咎　不用求谋　自在优游', '家宅  忧疑　自身  灾耗　求财  破失　交易  败　婚姻  不长　六甲  危险　行人  阻　田蚕  损　六畜  损　寻人  杳　公讼  亏　移徙  莫动　失物  疾病  即设送　山坟  不利', '霸王被困韩信率诸侯兵　与楚王大战於九里山　十面埋伏　围籍垓下　籍走至乌江　有高长牺舟以待曰　今独臣有船　王急渡江东　亦足王也　籍笑曰　江东子弟仝籍　渡江西诛秦无道　今无一人还　纵江东父兄　怜而王我　我独不愧於心乎　竟自羽(刎)而死', '本签者意滞之象。凡举事待时。且必须仔细小心。无一是处之时。目下不许君丝毫动之。一动不如一静。以目前之情况守之。不可勉强。一强求。逞一时之勇之时亏即主。弄巧成拙。无一可图利。易言之。守静安静。安分无忧不须贪望自有优游。');
INSERT INTO `guanyin` VALUES (67, '六七签', '上签', '金星试窦儿', '卯宫', '一条金线秤君心　无减无增无重轻　为人平生心正直　文章全贝艺光明', '此卦心平正直之象　凡事平稳无凶也', '心平正直　到底清平　只依本份　天下太平', '家宅  安　自身  平　求财  守慎　交易  稳　婚姻  合　六甲  男　行人  有信　田蚕  稳　六畜  安　寻人  迟　公讼  和　移徙  守旧　失物  见　疾病  禳星　山坟  平', '金星试窦儿', '本签之曰。心中端正之象。凡事能平稳大吉之者。一条金銔等君情。由它衡量即是无减无增。无重轻为人之道。即是人心平生心正直之时。即使不识文章也明白义之道理。易言之。平生正直善处人情只依本份天下则泰平矣。清高门路即可。');
INSERT INTO `guanyin` VALUES (68, '六八签', '中上签', '郭汾阳祝寿', '卯宫', '门廷吉庆喜非常　积善之门大吉昌　婚姻田蚕诸事遂　病逢妙药即安康', '此卦春梦百花之象　凡事遇贵人大吉也 门廷吉庆之象　凡事顺遂也', '春深日暖　百花自开　事莫迟疑　从人显达　积善之家　必有余庆　忠义之门　拱照福星', '家宅  旺　自身  安　求财  春旺　交易  遂　婚姻  成　六甲  防惊　行人  到　田蚕  利　六畜  旺　寻人  至　公讼  胜　移徙  如意　失物  急寻　疾病  安　山坟  大吉', '郭汾阳祝寿唐朝大将军　汾阳王郭子仪　做大寿　亲友来贺　气氛一片欢乐　喜气洋洋', '本签者。日暇风和之象。凡事大吉大利者。君尔之门庭清吉。梦也祯祥。古人劝之。积善之家庆有余。家运大吉大利也。积善者。先由心正。不存邪。助人为乐。修身。布施之谓。因之。讼理婚成蚕又熟。易言之。日暇风和百花大发事莫迟疑从今显达。');
INSERT INTO `guanyin` VALUES (69, '六九签', '中签', '梅开二度', '辰宫', '冬来岭上一枝梅　叶落枝枯终不摧　但得阳春悄急至　依然还我作花魁', '此卦梅花占魁之象　凡事宜迟则吉也', '一箭射空　当空不空　待等春来　彩在其中', '家宅  欠利　自身  作福　求财  谨慎　交易  待时　婚姻  迟成　六甲  春实秋虚　行人  迟至　田蚕  旺　六畜  旺　寻人  见　公讼  亏　移徙  失物  东方　疾病  虚惊　山坟  宜改', '梅开二度唐朝　一日梅花皆被风雨打落　梅良玉与陈公父女　拜求上天再开梅花一次　果梅花再开　是为梅开二度　其後梅良玉中元　终与陈杏元(陈公女)联姻', '本签者梅花占魁之象。凡事宜平吉利者也。冬至。岭上之一枝梅也。虽是叶落枯根。伊亦能傲霜斗雪疏影横斜。放出幽杳。为世人报得春之跫音将至。君多年之傲苦也。必得一报。出头天者。易言之。君之运也。一箭落空。非空不空看非得景致消息在中者。');
INSERT INTO `guanyin` VALUES (70, '七十签', '下签', '李密反唐', '辰宫', '朝朝恰似采花蜂　飞出西南又走东　春尽花残无觅处　此心不变旧行踪', '此卦蜜蜂采花之象　凡事劳心费力也', '无觅春色　久雨不晴　求谋动用　不如安静', '家宅  口舌　自身  小人　求财  破耗　交易  反覆　婚姻  不长　六甲  虚花　行人  杳　田蚕  损　六畜  损　寻人  无踪　公讼  亏　移徙  守旧　失物  空　疾病  有阻　山坟  改', '李密反唐隋末　瓦岗寨之首领　李密自封魏王　手下名将　如秦琼　单雄信　程咬金　纷投唐王李渊　李密与王伯当後亦投李渊　又娶李仁公之公主为妻　後杀妻反唐　被乱箭射死', '本签是游蜂采花之家。凡事劳而无功者。飞来飞去而复东。春届暮节花残也无其觅处。栖身之所。交易。婚姻。求财。六畜。生产。皆无一是处。在此苦境之时。最宜修吾身。积吾善。行布施。善吾之言行。方能有转机。易言之。役役终朝。暗雨不清。用尽机谋争加寂寞者。');
INSERT INTO `guanyin` VALUES (71, '七一签', '中签', '文君访相如', '辰宫', '认知仓龙十九卫　女子当年嫁二夫　自是一弓架两箭　切恐龙马上安居', '此卦一弓架两箭之象　凡事再合则吉也', '不出重重　事由天命　振六中开　切宜谨守', '家宅  损失　自身  是非　求财  劳力　交易  谨防　婚姻  再合　六甲  先虚后实　行人  阻　田蚕  晚收　六畜  损　寻人  见　公讼  亏　移徙  改　失物  疾病  禳星　山坟  改', '文君访相如汉朝　卓氏有女名文君　新寡　好文诗　听司马相如弹琴　遂起心　乘夜奔往相处　谈得投机　互倾恋慕　二人私奔出城　至成都结为夫妻　但家贫　两夫妇在临邛市　开设酒店　一个是才子　一个是佳人　两家都要当炉卖酒　司马相如後大贵', '本签之示意者。一弓两箭之象。凡事改变则大吉者。骑龙跨马上安居。凡事切莫堕志。修心向善去。自有好处。目下虽略困。但是。终必逢时。但勤劳。人事己尽天理。无不准。易言之。不审不审事由天命辰午中间切须谨慎。');
INSERT INTO `guanyin` VALUES (72, '七二签', '中签', '王莽求贤', '辰宫', '养蜂须用求他蜜　只怕遭触尾上针　须是眼前有异路　暗惊深如荆棘林', '此卦结蜂采蜜之象　凡事劳心费力也　求甜得苦之象　凡事防范灾殃也', '事须仔细　不用强求　结蜂采蜜　有甚来由', '家宅  欠利　自身  防　求财  阻　交易  迟　婚姻  阻隔　六甲  虚惊　行人  迟　田蚕  少收　六畜  少收　寻人  难　公讼  亏　移徙  莫动　失物  凶　疾病  设送　山坟  败', '王莽求贤汉　王莽依其姑母乃汉元帝之后　官封为新都侯　为人谦恭　又广结豪杰贤能　仗义疏财　後王莽篡汉　改国号曰　新　刘秀起来推翻王莽　恢复汉室　史称後汉', '本签者。示於**曰。事须仔细不可强求为蜂采蜜看其由来。事後遂意。皆祖宗积德之所致。若能顺理而行。益加培植。则天之报施。凡事先苦後甘之象。必须忍耐。否则无由成功。由忍一字。将有转吉化凶之时。');
INSERT INTO `guanyin` VALUES (73, '七三签', '上签', '陈桥兵变', '巳宫', '春来雷震百虫鸣　番身一转离泥中　始知出入还来往　一朝变化便成龙', '此卦雷发百虫之象　凡事遇贵人吉兆也', '雷发时转　出入两存　一朝变化　直到天门', '家宅  祈福　自身  平常　求财  春利　交易  贵人　婚姻  成合　六甲  男　行人  到　田蚕  早生　六畜  春利　寻人  见　公讼  胜　移徙  吉　失物  疾病  险阻　山坟  吉', '陈桥兵变宋太祖赵匡胤　仕局为检点　世宗崩　子恭帝立　仅数岁　(赵)奉命往伐河东　兵至陈桥　部下将士以主幼政乱　劝太祖即位　初不许　後乘太祖醉卧　以黄袍加体　乃引兵回东　受周恭帝禅位　(恭帝当年七岁　皇后新寡　兵变比禅让更合)', '本签示於**曰。是蛰中变态之象也。凡事变化则吉者。事事求变。舍旧图新。求合时潮。何必烦恼。一定可转机。多费心机。可有转变。君尔可知。功名富贵迟速有时。待时而动则吉也。易言之。震开蛰目忌入风云一好变态直入天宫。');
INSERT INTO `guanyin` VALUES (74, '七四签', '下签', '秦败擒三帅', '巳宫', '似鹄飞来自入笼　欲得番身却不通　南北东西都难出　此卦诚恐恨无穹', '此卦似鹄投水(笼)之象　凡事多虚少实也', '进身可(不)得　退步为(不)能　低头直去　悉在其中', '家宅  不安　自身  困滞　求财  小人　交易  破耗　婚姻  刑伤　六甲  刑劫　行人  阻　田蚕  缺　六畜  瘟　寻人  杳　公讼  祸　移徙  不利　失物  凶　疾病  延　山坟  绝', '秦败擒三帅秦穆公不听老臣蹇公之言　出师侵郑　与晋人战於　　秦师给晋国诱入险谷　前路被堵塞　两旁晋兵抛火种磺硝　後面晋兵杀声震天　秦军自相践踏　死伤无数　不敌而至大败　三个大元帅　俱皆被擒', '本签示之於**曰。野鸡投笼之象。既然是投笼者失去自由之意。当自守旧留心。今之命也。东西南北浑障碍者。此身憔悴。恨无穷之时。正逢不堪景象。凡事守之。慎之。三思而後行方可。易言之。进身不得退步而行。命运乖蹇。切勿交争。');
INSERT INTO `guanyin` VALUES (75, '七五签', '中签', '伍员夜出昭关', '午宫', '恰如拖虎过高山　战战竞竞胆碎寒　不觉忽然从好事　切须保守一身安', '此卦抱(拖)虎过山之象　凡事险凶惊恐也', '危危险险　前程有阻　若问求谋　到底辛苦', '家宅  祈保　自身  提防　求财  谨慎　交易  滞　婚姻  难顺　六甲  有惊　行人  阻　田蚕  五分　六畜  损　寻人  杳　公讼  亏　移徙  守　失物  难　疾病  祭虎　山坟  凶', '伍员夜出昭关伍子胥　春秋时代楚人　楚平王追杀之　日藏夜行　翻山越岭　至边境关卡韶关　守关的竟认不出伍员　因他终日焦虑　一夜白头　後平安到达吴国', '本签示之於**曰。宛如抱虎过山前之景也。令人战战竞竞瞻碎寒者。凡事必须多劈画。千万不得轻举妄动。机密不密则不成。须防口之血半。惟君尔之修身。积德功夫一至。必能逢得妙手。方能保得一身平安。易言之。危险阻隔前程切莫远行自宜守节。先凶後吉者。');
INSERT INTO `guanyin` VALUES (76, '七六签', '中签', '洪武看牛', '午宫', '鱼龙混杂意相同　耐守深潭待运通　不觉一朝头耸出　禹门一跳过龙宫', '此卦鱼龙未变之逸象　凡事待时至可也', '虽是滋基　也要待时　营谋自本　切莫妄为', '家宅  平安　自身  康泰　求财  有　交易  待时　婚姻  迟合　六甲  生男　行人  迟　田蚕  晚丰　六畜  尾利　寻人  未遇　公讼  延　移徙  守旧　失物  疾病  阻　山坟  发', '洪武看牛洪武即明太祖朱元璋　小时家贫　替人看牛　後当过和尚　又行乞度日　最终参军打败元兵　建立明朝　喻英雄莫问出处', '本签者禹门未透之象。凡百事变化则吉之意。鱼与龙两者混杂潭中意和同。耐守於潭却未济中时。唯有一朝头角耸之刻。君尔即可由禹门一跃到天宫去者。贵人逢遇之日。必有被擢拔之时。彼时商贾者财利勃发。家宅产业兴隆。另言之。虽有址地不如待时营谋出入切莫安为。');
INSERT INTO `guanyin` VALUES (77, '七七签', '中签', '捧璧归赵', '午宫', '梦中说得是多财　声名云外终虚来　水远山遥难信实　贵人指点笑颜开', '此卦梦中得宝之象　凡事虚多少实也', '如梦说梦　声名莫望　只待好时　贵人指示', '家宅  祈福　自身  阻　求财  虚　交易  难成　婚姻  不合　六甲  虚　行人  迟　田蚕  不收　六畜  损　寻人  杳　公讼  和　移徙  待时　失物  远　疾病  设送　山坟  改', '捧璧归赵　(完璧归赵)春秋时代　秦强赵弱　秦王欲十五城交换和氏璧　赵国大夫蔺相如携璧至秦　见秦王欲占不换此璧　蔺相如以砸碎和氏璧相要　最後终可携璧归赵', '本签之示於**者。梦中得财之象。亦即是空虚无力者。总而言之。身外浮名。总无可猜：水远山遥难信定。伊是遥远之地。无法信之。定之。贵人一指时。君尔笑颜逐开。易言之。梦说梦声名虚望只好待时贵人接引。');
INSERT INTO `guanyin` VALUES (78, '七八签', '上签', '临潼救驾', '午宫', '冷水未烧白沸汤　不寒不热有温凉　要行天下无他事　为有身中百艺强', '此卦平善用事之象　凡事平稳大吉也', '囚人逢赦　病遇良医　求财谋望　贵人指示', '家宅  安　自身  吉　求财  遂　交易  成　婚姻  合　六甲  男　行人  至　田蚕  熟　六畜  旺　寻人  至　公讼  散　移徙  大吉　失物  即见　疾病  安然　山坟  大吉利', '临潼救驾隋末　唐王李渊经过临潼山下　秦琼(叔宝)正于伍员庙午睡　被喊声吵醒　秦琼冲进重围　杀退敌人　救了王爷一家　即被唐王重用　成为唐朝之开国功臣', '本签即是平稳用事时凡事大吉利者。亦即是冷水来浇时也。白雪洋。时令不寒不热时自有温凉者。逢此时令时。君尔行天下时。并无他事。唯君汝。必须藏有一蛰之长久。可行天下无阻。易言之。囚徒可得久病遇良医。求财谋望贵人指迷。');
INSERT INTO `guanyin` VALUES (79, '七九签', '中签', '暗扶倒铜旗', '午宫', '虚空结愿保平安　保得身安愿不还　莫忘神圣宜还了　此知神语莫轻慢', '此卦信实莫信虚之　凡事守旧之兆也', '人有我见　心不用忙　观音指示　切莫轻慢', '家宅  还愿　自身  许经　求财  守份　交易  谨慎　婚姻  虚　六甲  生女　行人  滞　田蚕  七分　六畜  损　寻人  杳　公讼  和　移徙  守　失物  西方　疾病  解　山坟  西向', '暗扶倒铜旗隋末　东领关由杨义成镇守　并摆出铜旗阵　秦叔宝久攻不下　探知护阵主将罗成是他表弟　便联络姑母　命其子明保铜旗　暗助叔宝　秦叔宝破了铜旗阵　占领东岭关　得胜而归', '本签示之於**者。信实不虚凡事待时耐守则吉之灵签也。唯君尔虚空结愿结人缘。一人之力。有限者。如果逞一时之勇盲目猛冲。恐有难发生之时。时也命也。汝可悟之。叵逞勇矣。易言之。有人见我空自怀然佛慈指乐切莫轻谩。');
INSERT INTO `guanyin` VALUES (80, '八十签', '上签', '智远投军', '未宫', '直上仙岩要学仙　此知一旦帝王宣　青天日月常明照　心正声名四海传', '此卦贵人接引之象　凡事和合大吉也', '有始有终　莫向西东　心中用事　贵人重重', '家宅  平安　自身  吉利　求财  有利　交易  遂意　婚姻  双配　六甲  男　行人  回　田蚕  半收　六畜  胜　寻人  遇　公讼  胜　移徙  吉　失物  南　疾病  安　山坟  吉', '刘智远投军刘智远(知远)　沙陶(沙陀族)人　刘志远与妻李三娘居舅家　舅嫌其贫　哄志远往瓜园　欲给二瓜精吃掉　志远夜持枪　战瓜精走入地　掘开见剑　拾之辞妻投军　投靠岳勋　官至安抚使　　契丹灭晋　中原无主　智远于晋阳即帝位　改国曰后汉　仍称天福而不改元', '本签示之於**。之意为贵人性情之象也。凡百事遇贵人即化凶趋吉者。由诗意得知君尔直上高山去学化。岂知一旦帝王宣。即青天白日常明照。晴天无一片云。白日常照时。君之命运。正如当中。易言之。有始有终大问西中。心中用事贵人重重。');
INSERT INTO `guanyin` VALUES (81, '八一签', '上签', '风送滕王阁', '未宫', '梧桐叶落秋将暮　行客归程去似云　谢得天公高著力　顺风船载宝珍归', '此卦梧桐叶落之象　凡事先凶后吉也', '心中取事　天心从之　营谋用事　尽可施为', '家宅  祈保　自身  许经　求财  遂意　交易  成　婚姻  和合　六甲  虚惊　行人  动　田蚕  晚胜　六畜  稳　寻人  至　公讼  胜　移徙  随意　失物  疾病  喜　山坟  择地', '风送滕王阁唐朝咸淳年　南昌滕王阁重建成　王勃欲往　睡梦中水神曰送君一帆　醒来至江边　果见一船　上船後不久　即至滕王阁(离家七百里)　书滕王阁序王勃乃唐高宗朝间一秀士　姓王名勃字子安　祖贯山西晋州龙门人氏　善诗　有颂曰从来才子是神仙　风送南昌岂偶然　赋就滕王高阁句　便随仙仗伴中源　(中源指水神称中源水君　暗喻王勃成仙也)', '本签也。从心望之之象也。凡事大吉利者。一叶如秋後。逐渐有机缘。行客之来势如飞奔。风送君尔之船。只要轻轻著力。尔船即将宝藏船舶推送回来。不必费力者。此者非人力之所逮。易言之。心中所望。天必从之。所谋用事尽可施者也。');
INSERT INTO `guanyin` VALUES (82, '八二签', '中签', '火烧葫芦谷', '未宫', '炎炎烈火焰连天　焰里还生一朵莲　到底得成终不害　依然生叶长根枝', '此卦火里生莲之象　凡事似险非险也', '虽然逢险　不损其身　营谋用事　皆遇贵人', '家宅  欠安　自身  谨防　求财  劳力　交易  小人　婚姻  阻　六甲  虚惊　行人  阻　田蚕  缺　六畜  损　寻人  西　公讼  亏　移徙  守旧　失物  南　疾病  设送　山坟  改', '火烧葫芦谷三国　赤壁之战曹操大败　落荒而逃　孔明派大将于各处埋伏　曹操至葫芦谷口休息　见地势险要又无伏兵而哈哈大笑　忽闻炮响　张飞杀到　山谷到处起火　曹操急上马　狼狈奔逃　败走华容道', '本签者。如月初出之象。凡事举之时。得待时。来济时方吉者。君尔之今则是时运未济之时。今之不逢时叵逞强。欲图意外之财时。反而将失利者。宜控制之。不宜放任行之。汝必须待到十五良宵夜。月光瞑才可见。到清光。易言之。汝如是自足月初出直到圆时无有不吉者。');
INSERT INTO `guanyin` VALUES (83, '八三签', '中签', '李渊登位', '未宫', '譬若初三四五缺　半无半有未圆全　等待十五良宵夜　到处光明到处圆', '此卦月缺未圆之象　凡事守候忍耐也', '守旧待待时　如月初出　待等圆时　无有不吉', '家宅  许愿　自身  夏秋吉　求财  谨守　交易  待时　婚姻  迟　六甲  生女　行人  阻　田蚕  平稳　六畜  平稳　寻人  难　公讼  和　移徙  守旧　失物  难　疾病  设送　山坟  改', '李渊登位公元六一八年　隋炀帝在江都看琼花　被部下杀死　唐王李渊得知　即于太原登基称帝　国号唐　李渊(唐高祖)在位九年　由李世民传唐太宗)接位　喻境况渐入佳景', '本签者。如月初出之象。凡事举之时。得待时。来济时方吉者。君尔之今则是时运未济之时。今之不逢时叵逞强。欲图意外之财时。反而将失利者。宜控制之。不宜放任行之。汝必须待到十五良宵夜。月光瞑才可见。到清光。易言之。汝如是自足月初出直到圆时无有不吉者。');
INSERT INTO `guanyin` VALUES (84, '八四签', '下签', '庄子试妻', '未宫', '因名丧德如何事　切恐吉中变化凶　酒醉不知何处去　青松影里梦朦胧', '此卦寒鱼离水之象　凡事不可移动也', '寒鱼离水　美中不足　若问营谋　不如莫起', '此签六甲煞旺　诸事不利　禳灾保安', '庄子试妻周朝　姓庄名周　字子休　宋国蒙邑人　仕周为漆园吏　庄子假死试妻　其妻田氏守孝　七天後有一楚国王孙公子到访　自称庄子之徒　愿执百日之丧　田氏爱公子俊美　不足廿日成婚　公子叫心疼　曰脑髓(人心)可治　田氏往劈庄子棺以取脑髓(人心)　庄子破棺而出　後田氏羞愧自杀', '本签者因名丧德者。因之。凡事劳费心力而无益者也。为了攫取一己之名利。丧其德行。受世人辱骂。指摘者何用。神灵诫之曰。切忌忙中变作凶。本为善意。惟己之言行中不知不觉中。将善心变为恶而导致凶。易言之。君之命也寒鱼离水美中不足若问营谋如何结局。');
INSERT INTO `guanyin` VALUES (85, '八五签', '中签', '韩文公遇雪', '申宫', '云　雾罩山前路　万物圆中月再圆　若得诗书沉梦醒　贵人指引步天台', '此卦春尽花开之象　凡事主后改变也', '老者用求　春残再开　欲求名利　晚遇横财', '家宅  欠利　自身  谨慎　求财  著力　交易  平　婚姻  迟　六甲  女　行人  阻　田蚕  好　六畜  好　寻人  难　公讼  反覆　移徙  更新　失物  凶病　疾病  延滞　山坟  宜改', '韩文公遇雪韩愈　字退之　昌黎人　唐德宗贞元八年进士　　文公　因谏迎佛骨　独怒天子　贬至潮州刺史　途经蓝关　大雪封山　韩叹皇上不杀我　皇天亡我矣　事前其侄韩湘子曾曰　云横秦岭家何在　雪拥蓝关马不前　此时韩文公才悟其意　後韩愈弃官　财产尽捐地方　入山修道成仙', '本签是暮春百花盛开之时。凡百事求谋俱吉者也。沉滞已久之命运。於今逐渐入佳境。珍惜此时。把握本刻。立即动之。汝之运图将为尔辟坦途。亦即是万宝园中可再团。易言之。老者还来暮春风和欲求名利早晚遇财。');
INSERT INTO `guanyin` VALUES (86, '八六签', '上签', '商辂中三元', '申宫', '春来花发映阳台　万里车来进宝财　若得禹门三级浪　恰如平地一声雷', '此卦上朝见帝之象　凡事太吉大利也', '朝帝受职　如贫得宝　谋望从心　卦中第一', '此签从心所欲　诸事皆吉', '商辂中三元三元记　明朝　商辂　浙江人　父早亡　商辂三元及第　喻步步高升也　(三元即三级试　)乡试  解元省试  会元殿试  状元', '本签是帝朝受职之象。凡百事兴者遇贵则吉利者也。春来花发。是百花争妍斗艳之刻。万里舟行进宝来其声声之旺也。如跃过禹门三级浪。恰如平地一声雷响起者。易言之。帝朝受职如贪得宝望用从心签中第一耶。');
INSERT INTO `guanyin` VALUES (87, '八七签', '中签', '咬金探地穴', '申宫', '人行半岭日西山　竣岭崖岩未可安　仰望上天为护佑　此身犹在太平间', '此卦淘沙见金之象　凡事有贵人之兆也', '凿石见玉　淘沙见金　须要著力　只是劳心', '家宅  祈福　自身  欠利　求财  劳心　交易  谨慎　婚姻  有阻　六甲  虚　行人  迟　田蚕  平稳　六畜  平稳　寻人  未遇　公讼  亏　移徙  守　失物  凶　疾病  阻　山坟  不阻', '咬金探地穴为庆战功　程咬金于演武杨摆宴　忽地裂一洞　深不可测　大家拈字抽签　咬金抽得　租绳缚身入穴　见石造之门桌椅桥等　桌上还有龙袍玉带　咬金穿著出洞　做了三年混世魔王', '本签者。是淘沙见金之象也。凡百事劳心即有成之命运。目下居於日衔山之境。且前面正遇崇山峻岭。艰苦历程中。苦中不得不仰头祈求上苍垂护佑。己身犹在太虚之间。唯劳心可成之命。易言之。剖石见玉。淘沙见金。须用著力劳心可成者也。');
INSERT INTO `guanyin` VALUES (88, '八八签', '中签', '庞洪畏包公', '酉宫', '林为一虎在当门　须是有威不害人　分明说是无防事　忧恼迟疑恐惊心', '此卦林木虎有威之象　凡事虚惊少实也', '炉头点雪　似耳边风　可宜作福　后吉前凶', '家宅  不安　自身  口舌　求财  小人　交易  不利　婚姻  不合　六甲  虚险　行人  阻　田蚕  损　六畜  损　寻人  杳　公讼  亏　移徙  守旧　失物  凶　疾病  祭虎　山坟  凶', '庞洪畏包公宋朝仁宗　包公为开封府府尹　庞洪为国丈(皇帝作女婿)　陷将军狄青于狱中　包公求审此案　庞洪惊畏万分　多方阻拦　後案情大白　证实庞洪诬陷　呈宋仁宗圣裁　又因皇戚被释', '本签为木虎虚惊之家。凡百事宜守待事方吉安。千万叵倚强凌弱。远望威容意不禁者。即使木虎不必畏惧。但己身如同入大江心。岌岌可危。必须防贪求不已。生祸福无不自求者。必慎之。易言之。炉头点雪耳边过风可宜作福前凶後吉也。');
INSERT INTO `guanyin` VALUES (89, '八九签', '上签', '大看瑶花', '酉宫', '出入营谋大吉昌　似玉无瑕石里藏　若得贵人来指引　斯时得宝喜风光', '此卦石藏珍宝之象　凡事称心大吉也', '如石藏玉　贵人指示得祗称心　欢怀得意', '家宅  吉利　自身  康泰　求财  遂　交易  成　婚姻  成就　六甲  生男　行人  动　田蚕  著力　六畜  勤理　寻人  见　公讼  胜　移徙  吉　失物  在　疾病  安　山坟  吉', '大看瑶花　(琼花/昙花)琼花年开一次　且在晚间隋炀帝　听得扬州有琼花　即动用民工开凿运河　南下扬州看花　被杀隋亡', '本签者玉藏石中之象。凡事称心大吉昌者也。灵签曰。出入营谋大吉昌。君今逢无瑕之玉藏石中。能获贵人指点。扶持。终能得宝。此时此刻当喜非常。令人雀跃者。惟称心时不可大意者。更不忘修德。积善。易言之。本签为如玉藏石贵人指出而得宝称心遂意大吉者。');
INSERT INTO `guanyin` VALUES (90, '九十签', '上签', '苇佩遇仙', '酉宫', '忽言一信向天飞　泰山宝贝满船归　若问路途成好事　前头仍有贵人推', '此卦功名成就之象　凡事宜进大吉也', '若问功名　前有贵人　更添喜气　财禄丰盈', '此签诸事佳吉　求之顺遂', '苇佩遇仙　(韦佩遇仙)穷书生韦佩年近四十　牵驴过洛阳桥　一渔夫卖大鳖　韦佩不忍　但缺钱　遂以驴换鳖　于桥下放生　从此韦佩中举　加官晋爵　或指此鳖为仙所化　故曰韦佩遇仙', '本签者功名成就之象。凡事进身大吉雀也。君尔之今连也。忽鞘一信天下鞘之时。宝贝船装满截归之时若问前程於神仙。君之今也。始终应得贵人提之刻。凡百事得真人。顺利推进之运。上上者。易言之。君汝之今。欲问功名贵人遇得更增福庆财禄进益者耶。');
INSERT INTO `guanyin` VALUES (91, '九一签', '中签', '三战吕布', '酉宫', '好展愁眉出众来　前途改变喜多财　一条大路如天阔　凡有施财尽畅怀', '此卦前途显达之象　凡事通泰大吉也', '一条大路　可向前行　心中用事　只可向前', '此签诸事如心　顺利吉昌', '三战吕布三国时　吕布领兵三万入驻虎牢关　张飞持三叉戟与吕布持丈八矛相战　不分胜负　关羽持青龙偃月刀加入　刘备又加入战圈　是为三英战吕布　吕布见不能胜　逃回关内', '本签为前程有坦途可行之象。凡事谋之行之立见吉利者也。一条坦途可向前行。心中用事不必瞻前顾後。勇往迈进事事可如意。顺利吉昌者。唯行在顶峰傲。视下界之际。勿忘今日之所由生。皆由贵人扶之者。易言之。必留个余地。以防向下坡之崎岖。');
INSERT INTO `guanyin` VALUES (92, '九二签', '上签', '蔡卿报恩', '酉宫', '自幼为商任设谋　财禄盈丰不用求　若是双身谋望事　秀才出去状元回', '此卦自小为商之象　凡事勤俭无忧也', '自小至大　无阻无碍　作意营谋　功名顺遂', '家宅  安　自身  泰　求财  顺遂　交易  胜　婚姻  合　六甲  生男　行人  动　田蚕  半收　六畜  旺　寻人  见　公讼  胜　移徙  如意　失物  在　疾病  即安　山坟  大吉', '蔡卿报恩　(蔡邕)蔡卿即蔡伯喈　上京赴考　其妻赵五娘　因家贫以糟糠果腹　乡邻张广才时加接济　蔡伯喈中了状元　回家得知其妻获张广才相帮　夫妻便到张家拜谢　并赠银两', '本签者。进身大吉之象也。凡百事兴时操心大利。灵诗示之云。经商得利称心怀。福禄面也。荣华倍获财。君汝如问谋望之面。神可告之君。必能一举状元衣锦还乡者。易言之。汝自幼小之年。自小至大无相无碍诸事遂古。遂望自在耶。');
INSERT INTO `guanyin` VALUES (93, '九三签', '中签', '高君保招亲', '戌宫', '鸾凤翔毛雨淋漓　当时却被雀儿欺　惊教一日云开远　依旧还君整羽衣', '此卦鸾凤被雨之象　凡事待时大利也', '小人日盛　君子莫为　只宜守己　待时施为', '家宅  欠利　自身  谨防　求财  阻　交易  待时　婚姻  迟滞　六甲  有虚　行人  留连　田蚕  晚　六畜  损　寻人  难　公讼  亏　移徙  守旧　失物  凶　疾病  延　山坟  改', '高君保招亲　(高俊保)高君保为高怀德之子　赵匡胤领兵征南唐被困　高君保领兵二百押粮接济　至双锁山　被女贼刘金定劫持　并强逼成亲　夫妻领众杀入重围　救出赵匡胤', '本签示之於**曰。未用君子之象。凡事先难。因谋事之敏。终获吉利者也。君汝之目下命。人在穷困之际。啻好屈已忍之。强欢言笑。千万叵急躁恼怒。斯时四面楚歌。皆陷阱也。易言之。小人正盛君子莫为暂时守己自有亨通之时也。');
INSERT INTO `guanyin` VALUES (94, '九四签', '下签', '伯牙访友', '戌宫', '君子莫体小人为　事若差池惹是非　琴鸣须用知音听　守常安静得依稀', '此卦要逢知己之象　凡事守常则吉也', '知音方许　闲事莫理　待得贵人　无不欢喜', '家宅  慎出　自身  小人　求财  待时　交易  谨慎　婚姻  宜迟　六甲  女　行人  动　田蚕  少　六畜  损　寻人  勤　公讼  和　移徙  失物  速寻　疾病  阻隔　山坟  不利', '伯牙访友　(伯牙碎琴)春秋战国　姓俞名瑞字伯牙　楚国郢都人　事晋退休　乘船遇荒山　见樵夫子期欣赏其琴　遂结契兄弟　相约一年後重叙　到期再往访　才知锺子期已死　往访其坟　断弦碎琴　曰摔碎瑶琴凤尾寒　子期不在对谁弹　春风满面皆朋友　欲觅加音难上难', '本签者。君尔未遇知音之象也。凡事耐性行之。为之则趋吉也。平素多信仰神佛扶之。能遇贵人之时。君即可争如定静得便宜耶。易言之。汝在功行圆满之刻。必能光大门户。知音行正百事莫理若人遇贵无价珍美耶。');
INSERT INTO `guanyin` VALUES (95, '九五签', '中签', '曹丕称帝', '戌宫', '志气功业在朝朝　今将酒色不胜饶　若见金鸡报君语　钱财福禄与君招', '此卦志气功名之象　凡事守常大吉也', '志气心动　守己未迟　金鸡报诺　无不欢喜', '家宅  祈保　自身  中平　求财  劳心　交易  早断　婚姻  有成　六甲  安　行人  田蚕  晚成　六畜  成　寻人  难　公讼  急断　移徙  守　失物  远　疾病  延缠　山坟  吉', '曹丕称帝曹丕为曹操之次子　曹操挟天子(汉献帝)以令诸侯　曹丕废献帝自立　改国号为魏　七年後又被手下司马炎废掉　改国号为晋', '本签之曰功名待时之象。凡百事必就大利者耶。上上之签。百事皆有遂意之可能。繇於祖德之深。君尔又顺理而行。益加倍植。则天之报应。具有复泰之象。占此签之君慎守之。易言之。尔之今也事业功名守已待时金鸡终叫即时运亨通之刻耶。');
INSERT INTO `guanyin` VALUES (96, '九六签', '上签', '窦燕山积善', '戌宫', '巍峨宝塔不寻常　八面玲珑尽放光　劝君立志勤顶礼　作善苍天降福祥', '此卦福德现身之象　凡事大吉利也', '这些福份　众人皆有　不用心忙　福德前定', '家宅  许愿　自身  许经　求财  正胜　交易  大吉　婚姻  好合　六甲  喜　行人  动　田蚕  平稳　六畜  欠利寻人  遇　公讼  和　移徙  吉　失物  疾病  禳星　山坟  平', '窦燕山积善宋朝　窦燕山中年无子　一夜梦中　其父训他去恶从善　自此窦燕山痛改前非　力行方便　并得五子　三字经有载窦燕山　有义方　教五子　名俱扬', '本签也。福德显现之象也。凡百事营谋可获利者耶。君之前。此宝塔为耸立云宵巍巍者。八面均放出异彩。啻要君志心勤顶礼。天龙。此吉祥之天兽。为君汝降天祥者。易言之。福田诸人皆现可用诚心。福德即到。');
INSERT INTO `guanyin` VALUES (97, '九七签', '中签', '六出祁山', '亥宫', '当风点烛空疏影　恍惚铺成镜里花　累被儿童求收拾　怎知只是幻浮槎', '此卦当风点烛之象　凡事虚名不利也', '富贵在天　贫穷是命　不用求谋　皆是前定', '家宅  祈福　自身  谨慎　求财  得利　交易  慎重　婚姻  中平　六甲  有惊　行人  杳　田蚕  损　六畜  损　寻人  无踪　公讼  亏　移徙  守旧　失物  凶　疾病  犯阴　山坟  改', '六出祁山三国　祁山横亘于魏蜀间　孔明屡出祁山伐魏不果　第六次出祁山　孔明写了出师表　终不能胜　且死于五丈原　喻时也命也　到头来一切皆空', '本签也。虚名薄利之象。凡百事不得贪求者也。诗日：悦若铺地满地琼。岂知收拾总不成。如君之行动不紧速。将闹得无法收拾。遥言四播。到头来将陷入罗网中自拔不得。如此苦境只有几人知。易言之。君目下之运也。该知富贵在天。贫穷由命不用机谋。皆有分定者耶。');
INSERT INTO `guanyin` VALUES (98, '九八签', '下签', '吉平遇难', '亥宫', '出入求谋事宜迟　急恐忧愁惹是非　如鸟飞入罗网内　脱困能有几多时', '此卦守旧随缘之象　凡事不如意主凶也', '守旧随时　待贵人至　若问诸般　主事定迟', '家宅  忧疑　自身  小人　求财  破　交易  损失　婚姻  不长　六甲  刑损　行人  困　田蚕  损　六畜  损　寻人  杳　公讼  亏　移徙  勿动　失物  凶　疾病  犯罗网　山坟  绝', '吉平遇难三国　医师吉平　感于曹操挟天子以令诸侯　欲杀奸臣　恰曹操头痛　吉平往治　药中下毒　曹操发觉　严刑拷打吉平　吉平大骂曹操　一头撞于石柱自杀', '本签者。禽遭罗网之象。凡百事守旧可获安者。诗曰。经商出入事迟疑。也因此惹闲愁。不必要之愁争。听了是是非非。谣言四播。逢遇如此罗网苦境中。必须多修幅。积德方能脱离险境者耶。易言之，君之今也，禽遭罗网有理难分。随缘守旧遇贵乃成之命耶。');
INSERT INTO `guanyin` VALUES (99, '九九签', '下签', '陶三春挂帅', '亥宫', '勒马持鞭直过来　半有忧危半有灾　恰似遭火焚烧屋　天降时雨荡成灰', '此卦半忧半喜之象　凡事只宜行善也', '终身劳力　莫管是非　枷锁临时　怨尤自至', '家宅  祈保　自身  守慎　求财  破　交易  阻　婚姻  刑克　六甲  险　行人  困　田蚕  损　六畜  损　寻人  招非　公讼  亏　移徙  守旧　失物  凶　疾病  忧危　山坟  败', '陶三春挂帅宋朝　陶洪之女　陶三春与郑恩比武成亲　生子郑印　娶妻王玉娥　皆习武　後闻郑恩与赵匡胤出兵南唐被围　陶三春带同子媳攻打南唐　两师会合　得胜而回', '本签示之於**者凶危有之象。凡事凶後吉耶。等闲骑马著马鞭来。半有忧危半有财。似火方烧头上屋。正受灾殃之刻。一场大雨来了之後。方可将灾厄洗清而去。目下虽事难成。劳心劳力之後。必有转安之时。易言之。君汝也。知如不知。不管是非临枷锁恩赦放归。');
INSERT INTO `guanyin` VALUES (100, '一佰签', '下签', '三教谈道', '亥宫', '佛神灵变与君知　痴人说事转昏迷　老人求得灵签去　不如守旧待时来', '此卦守常勿动之象　凡事宜待时吉也', '完满灵签　尽报君加　信旧勿动　待等时至', '此是满签　诸事不利　大作福力　可保平安', '三教谈道北周武帝时　三教(儒释道)互相排斥　皇帝召集大会辩论　经过三次争辩　没得结论　喻道在我教　理存我方　争执不止　痴事一桩', '本签耶。无定之象。外虚少实者也。第一百首。是盈数。为全数之末。亦即言。它是前数之终。後数之始。易言之。黑暗将去。黎明到来之徵。表面上是没落之相。其实是通亨衢之意。唯集福之本。宜小心集之。及百析之未然营谋用事费力流连');
